INSERT INTO `brands` (`id`, `title`, `slug`, `description`, `service_id`, `portfo_id`, `comment`, `countview`, `tags`, `images`, `status`, `indexshow`, `sort`, `created_at`, `updated_at`) VALUES
(4, 'تب کار', 'تب-کار', 'شرکت تب کار وابسته به شرکت رسام اندیشه پارس شرکتی تبلیغاتی است جهت تبلیغات بر روی بدنه ی تاکسی ها و خودرو های عمومی', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/0510b582195d6a83787865a9aeb63ee935.png', 1, 1, 12, '2020-09-16 08:43:28', '2020-09-20 13:15:50'),
(5, 'هیبا', 'هیبا', 'فروشگاه مد و لباس هیبا در زمینه پوشاک اسلامی و حجاب استایل به صورت آنلاین در اینستاگرام و بصورت حضوری در پاساژ گالریا محصولات خود را به فروش میرساند', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/74e08bde504cb6da7f9901582e6ec6d242.png', 1, 1, 11, '2020-09-16 08:46:16', '2020-09-24 09:35:26'),
(6, 'سنگ باما', 'سنگ-باما', 'سنگ باما ، وب سایت مرجع سنگ های ساختمانی که صرفا برای آشنایی با سنگ های ساختمانی ، آشنایی با انواع سنگ و همچنین کم کردن فاصله میان خریداران و فروشندگان سنگ شکل گرفته است.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/5d6cf57114a38cdc58452a9eee4a1b9714.png', 1, 1, 13, '2020-09-16 08:48:01', '2020-09-20 13:16:39'),
(7, 'ایران مرمر', 'ایران-مرمر', 'کارخانه سنگ ایران مرمر:\r\nسال تولید: ۱۳۴۴ \r\nموسس: حسن زنگنه\r\nفن آوری پیشرفته، خلاقیت، و کیفیت در پردازش سنگ طبیعی مورد توجه اصلی ایران مرمر است.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/71ea2098ee4d8d3d349fd4158314ed2123.png', 1, 1, 10, '2020-09-16 08:48:37', '2020-09-20 13:17:13'),
(8, 'سانگ یانگ', 'سانگ-یانگ', 'سانگ یانگ، چهارمین شرکت خودرو سازی بزرگ کره ای، که در سال  ۱۹۶۳ است تاسیس شده است.\r\nخودروهایی با نام های موسو، چیرمن، اکتیون، کوراندو، کایرون، تیوولی و... تولید شده از شرکت خودروسازی سانگ یانگ، تا به امروز در ایران عرضه شده اند.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/ed89edcc50c1cd89304ff2b2fe73427011.png', 1, 0, NULL, '2020-09-16 08:51:05', '2020-09-20 13:17:32'),
(9, 'مادیران', 'مادیران', 'شرکت مادیران در سال ۱۳۴۳ تاسیس شده و در حال حاضر یکی از معتبرترین شرکت های حوزه IT و تجهیزات الکترونیکی در ایران است. سه برند X.vision و  kisabz. و Sharp برای این شرکت می باشد.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/eed4c8453a1303a478cfa043524decd016.png', 1, 0, NULL, '2020-09-16 08:54:30', '2020-09-20 13:17:53'),
(10, 'آمیتیس سنتر', 'آمیتیس-سنتر', 'آمیتیس سنتر، با هدف فروش کالا های دیجیتال با قیمت های مناسب، واقع در شهر تهران، در خوزه خرید و فروش لوازم دیجیتالی (همچون موبایل، تبلت و...) فعالیت می کند.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/99620cef640900e9e67199099fe8840821.png', 1, 0, NULL, '2020-09-16 08:57:13', '2020-09-20 13:18:50'),
(11, 'بانک شهر', 'بانک-شهر', 'بانک شهر یکی از مدرن ترین و قوی ترین بانک های ایرانی اسلامی است که در سال ۱۳۸۷ به عنوان یکی از مجموعه های شهرداری تهران تاسیس شد.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/d06378ec887763270569b2f2338b8c789.png', 1, 1, 6, '2020-09-16 09:08:58', '2020-09-20 13:19:05'),
(12, 'کانون هم عهد', 'کانون-هم-عهد', 'وب سایت هم عهد وب سایت رسمی استاد محمد باقر حیدری کاشانی می باشد که فعالیت ان در زمینه مهدویت و سبک زندگی مهدوی است.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/ad9dc5a0f02fde3e4ddf5467136d74f824.png', 1, 0, NULL, '2020-09-16 09:10:06', '2020-09-20 13:19:28'),
(13, 'پالادیوم', 'پالادیوم', 'مرکز خرید پالادیوم یکی از زیباترین و بزرگترین پاساژ های تهران با متنوع ترین برند های روز دنیا واقع در ولنجک خیابان الف می باشد.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/7d5556ce2c18960fcc1055b1446000191.png', 1, 1, 9, '2020-09-16 09:38:01', '2020-09-20 13:19:43'),
(14, 'مهساب سنگ', 'مهساب-سنگ', 'با قدمتی ده ساله و متکی بر تجربه و دانش و مهارت به روز دنیا علم و هنر را برای بدست آوردن بهترین نتیجه در زمینه تولید سنگ به خدمت گرفته است.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/d7a0e68e5ce0943e4f7793d75ceb3def28.png', 1, 0, NULL, '2020-09-16 09:40:58', '2020-09-20 13:20:05'),
(15, 'آژانس مهاجرتی آوانا', 'آژانس-مهاجرتی-آوانا', 'آژانس مهاجرتی آوانا', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/1475741751755c78d0b29dd771d4a5ec37.png', 1, 1, 15, '2020-09-16 09:56:31', '2020-09-27 07:41:26'),
(16, 'واکس بلدرچین', 'واکس-بلدرچین', 'واکس بلدرچین بزرگترین تولید کننده انواع واکس در ایران و صادرکننده به بازارهای بین المللی است.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/f38c8d5e1b724fff661e0409ed91bb8c25.png', 1, 1, 5, '2020-09-16 10:07:07', '2020-09-20 13:20:49'),
(17, 'جیلی', 'جیلی', 'یکی از شرکت های خصوصی خودرو سازی چینی است.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/85b5333a16357d6c45a7023b901ca4a422.png', 1, 0, NULL, '2020-09-16 10:23:26', '2020-09-20 13:21:09'),
(18, 'سمعک برتر', 'سمعک-برتر', 'کلینیک تخصصی شنوایی سمعک برتر، نمایندگی فروش انواع سمعک ها با قیمت های مناسب هستند که واقع در خیابان شریعتی  فعالیت می کنند.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/3ece62bb0e51c3056469fbfcddb85a4333.png', 1, 0, NULL, '2020-09-16 10:23:54', '2020-09-20 13:21:58'),
(19, 'پزشکان بدون مرز', 'پزشکان-بدون-مرز', 'این تیم پزشکی تقریبا در بیشتر کشور های جهان فعالیت دارد.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/961bff29e3f2bbc9d929d0c78288c4d18.png', 1, 0, NULL, '2020-09-17 06:24:23', '2020-09-20 13:22:59'),
(20, 'داروخانه دکتر صوفی بروجردی', 'داروخانه-دکتر-صوفی-بروجردی', 'داروخانه دکتر بروجردی واقع در خیابان پیروزی خیابان نبرد پاسخگوی نیاز های شما به داروهای مختلف است.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/b7bdc1b1150ac94ae2d9a768bc56787620.png', 1, 0, NULL, '2020-09-17 07:03:53', '2020-09-20 13:23:27'),
(21, 'صندوق ذخیره کارکنان بانک شهر', 'صندوق-ذخیره-کارکنان-بانک-شهر', 'در خرداد 1395 تفکر افزایش سطح رفاه و معیشت پرسنل بانک شهر و شرکت های تابعه و افزایش قدرت خرید منجر به تاسیس صندوق ذخیره کارکنان بانک شهر.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/51641cc7ed1f2b33f0a0cd3e7f28fbb541.png', 1, 0, NULL, '2020-09-17 07:17:04', '2020-09-24 09:03:30'),
(22, 'تیم بسکتبال پارسیا', 'تیم-بسکتبال-پارسیا', 'تیم بسکتبال پارسیا ویژه نونهالان و نوجوانان در لیگ بسکتبال ایران است.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/3bbe6860ef68cc0366ba2d4f222c91aa26.png', 1, 1, 14, '2020-09-17 07:30:40', '2020-09-20 13:24:04'),
(23, 'عکس مستند', 'عکس-مستند', 'جشنواره عکس مستند هر ساله برای شناسایی افراد با استعداد در زمینه عکاسی مستند برگزار می شود.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/681fb23db1b60be9e3c979ca1463ca3932.png', 1, 0, NULL, '2020-09-17 07:43:55', '2020-09-20 13:24:18'),
(24, 'ماشین آلات اداری آسماری', 'ماشین-آلات-اداری-آسماری', 'ماشین آلات اداری آسماری عرضه کننده ماشین آلات اداری از جمله دستگاه کپی و پرینتر است.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/776e8049cc3f9562cf6b7f2d189cf20434.png', 1, 0, NULL, '2020-09-17 07:51:30', '2020-09-20 13:24:35'),
(25, 'گروه طراحی مد و لباس محرر', 'گروه-طراحی-مد-و-لباس-محرر', 'با هدف تولید ملی در صنعت پوشاک و مد اسلامی فعالیت می کند.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/4c8e76de24f6405a685c165bc0431e7938.png', 1, 1, 8, '2020-09-17 08:50:45', '2020-09-20 13:24:50'),
(26, 'محصولات غذایی رنیس', 'محصولات-غذایی-رنیس', 'کارخانه محصولات غذایی رنیس با تکیه برتوان داخل به تولید انواع ادویه جات میپردازد.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/ed2ebfe82991e710349ccebb7092f12340.png', 1, 1, 4, '2020-09-17 09:01:54', '2020-09-20 13:25:02'),
(27, 'آسا تجارت', 'آسا-تجارت', 'شرکت آسا تجارت در زمینه صادرات کالا و خدمات در عرصه های بین المللی فعالیت می کند.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/55bcffa2c88d6b5a9006d45ef790bf0530.png', 1, 0, NULL, '2020-09-17 09:05:36', '2020-09-20 13:25:16'),
(28, 'کوزه', 'کوزه', 'رستوران سنتی کوزه با فراهم کردن محیطی دلنشین و کیفیت بی نظیر غذاها پذیرای حضور گرمتان خواهد بود.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/cab4a1d2677110a63804f38ee48d24d313.png', 1, 0, NULL, '2020-09-17 09:11:07', '2020-09-20 13:25:29'),
(29, 'آینده ایمن پیشگامان', 'آینده-ایمن-پیشگامان', 'شرکت آینده ایمن پیشگامان شرکتی است در زمینه خدمات امنیتی مانند دوربین های مدار بسته ، آنتی ویروس و... فعالیت می کند.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/9e4f90a74c5c551632231e1d72607de54.png', 1, 0, NULL, '2020-09-17 09:16:15', '2020-09-20 13:25:42'),
(30, 'فرهاد اکسچنج', 'فرهاد-اکسچنج', 'صرافی فرهاد اکسچنج از بروزترین و کارآمدترین صرافی های تهران است معاملات خود را به صرافی فرهاد اکسچنج بسپارید.', NULL, NULL, NULL, NULL, NULL, 'images/brand/2020/e6870ebf4b6d559bd76888ea02d0c5ce19.png', 1, 0, NULL, '2020-09-17 09:22:25', '2020-09-20 13:25:56'),
(34, 'کارخانه سنگ دومارین', 'کارخانه-سنگ-دومارین', 'دومارین یکی از کارخانه جات بزرگ سنگ و برش در تهران است که به صورت حرفه ای به تولید سنگ ساختمانی و صادرات آن ها م یپردازد. مدیر این کارخانه برای نمایشگاه سنگ های ساختمانی در ترکیه به شرکت بستا مراجعه کردند، طی جلسات مشاوره نیاز سنجی انجام شد و استراتژی برند تدوین شد و در مرحله بعد لوگو و هویت سازمانی برند خلق شد.\r\nاز دیگر نمونه کارهای شرکت بستا برای این برند، طراحی کاتالوگ و ست اداری کامل است.', NULL, NULL, NULL, NULL, 'دومارین، کارخانه، سنگ، ساختمانی، صادرات، ترکیه، مشاوره، استراتژی، لوگو، هوی سازمانی، طراحی، کاتالوگ، ست اداری', 'images/brand/2020/5f51591b9c2df79e60f1ddc5a773d05f5.png', 1, 0, NULL, '2020-09-26 08:24:21', '2020-09-27 16:06:38'),
(35, 'هلدینگ حقوقی دادورزان امین', 'هلدینگ-حقوقی-دادورزان-امین', 'هلدینگ حقوقی دادورزان امین مطرح ترین و خوش نام ترین های استان تهران است \r\nبعد از جلسه ای که با مدیریت این هلدینگ برگزار شد تیم بستا نیاز سنجی و استراتژی نویسی این کار را شروع کردند و نتیجه ی آن خلق برند داد ورزان امین شد.\r\nما برای این هلدینگ:\r\nاستراتژی نویسی، طراحی وب سایت اختصاصی، طراحی لوگو و هویت سازمانی، طراحی استرابورد تبلیغاتی انجام داديم ونتیجه ی کار برای کارفرما بسیار مطلوب بود.', NULL, NULL, NULL, NULL, 'هلدینگ حقوقی دادورزان، تیم بستا، نیاز سنجی، استراتژی نویسی، طراحی وب سایت اختصاصی، طراحی لوگو، طراحی استرابورد،هویت سازمانی.', 'images/brand/2020/29c0922ad6d632dc36fac41fe621608a12.png', 1, 1, 7, '2020-09-26 08:40:33', '2020-09-27 16:06:48'),
(37, 'مجمع کارآفرینان غدیر', 'مجمع-کارآفرینان-غدیر', 'مجمع کارآفرینان غدیر', NULL, NULL, NULL, NULL, 'کارآفرینان غدیر', 'images/brand/2020/cd7d25d8f7c306c3899c9d48f59ca92239.png', 1, 1, 3, '2020-09-27 06:25:19', '2020-09-27 16:07:40'),
(38, 'خانه مستند انقلاب اسلامی', 'خانه-مستند-انقلاب-اسلامی', 'خانه مستند انقلاب اسلامی', NULL, NULL, NULL, NULL, 'خانه مستند انقلاب اسلامی', 'images/brand/2020/a42ce4b03693b74cfae8dc59d3b8604417.png', 1, 1, 2, '2020-09-27 06:38:54', '2020-09-27 16:07:51'),
(39, 'لوازم التحریر اسکول فنس', 'لوازم-التحریر-اسکول-فنس', 'لوازم التحریر اسکول فنس', NULL, NULL, NULL, NULL, 'اسکول فنس', 'images/brand/2020/444ad18890ee3f0a99f655e3ef872e463.png', 1, 0, NULL, '2020-09-27 08:00:36', '2020-09-27 16:08:13'),
