(7, 70, 'شرکت نوسان الکتریک', 'customers/images/7.png', 4, NULL, 0, 1, 4, NULL, NULL),
(8, 80, 'پزشکان بدون مرز', 'customers/images/8.png', 4, 'این تیم پزشکی تقریبا در بیشتر کشور های جهان فعالیت دارد.', 0, 1, 4, NULL, NULL),
(9, 2, 'بانک شهر', 'customers/images/9.png', 4, 'بانک شهر یکی از مدرن ترین و قوی ترین بانک های ایرانی اسلامی است که در سال ۱۳۸۷ به عنوان یکی از مجموعه های شهرداری تهران تاسیس شد.', 0, 1, 4, NULL, NULL),
(10, 50, 'صنایع روشنایی مدیس', 'customers/images/10.png', 4, NULL, 0, 1, 4, NULL, NULL),
(11, 7, 'نمایندگی سانگیانگ', 'customers/images/11.png', 4, 'سانگ یانگ، چهارمین شرکت خودرو سازی بزرگ کره ای، که در سال  ۱۹۶۳ است تاسیس شده است.\nخودروهایی با نام های موسو، چیرمن، اکتیون، کوراندو، کایرون، تیوولی و... تولید شده از شرکت خودروسازی سانگ یانگ، تا به امروز در ایران عرضه شده اند.', 0, 1, 4, NULL, NULL),
(12, 19, 'هلدینگ حقوقی دادورزان امین', 'customers/images/12.png', 4, 'هلدینگ حقوقی دادورزان امین مطرح ترین و خوش نام ترین های استان تهران است \nبعد از جلسه ای که با مدیریت این هلدینگ برگزار شد تیم بستا نیاز سنجی و استراتژی نویسی این کار را شروع کردند و نتیجه ی آن خلق برند داد ورزان امین شد.\nما برای این هلدینگ:\nاستراتژی نویسی، طراحی وب سایت اختصاصی، طراحی لوگو و هویت سازمانی، طراحی استرابورد تبلیغاتی انجام داديم ونتیجه ی کار برای کارفرما بسیار مطلوب بود.', 0, 1, 4, NULL, NULL),
(13, 51, 'کوزه', 'customers/images/13.png', 4, 'رستوران سنتی کوزه با فراهم کردن محیطی دلنشین و کیفیت بی نظیر غذاها پذیرای حضور گرمتان خواهد بود.', 0, 1, 4, NULL, NULL),
(14, 24, 'سنگ باما', 'customers/images/14.png', 4, 'سنگ باما ، وب سایت مرجع سنگ های ساختمانی که صرفا برای آشنایی با سنگ های ساختمانی ، آشنایی با انواع سنگ و همچنین کم کردن فاصله میان خریداران و فروشندگان سنگ شکل گرفته است.', 0, 1, 4, NULL, NULL),
(15, 15, 'شرکت سریر ترابر', 'customers/images/15.png', 4, NULL, 0, 1, 4, NULL, NULL),
(16, 10, 'شرکت مادایران', 'customers/images/16.png', 4, 'شرکت مادیران در سال ۱۳۴۳ تاسیس شده و در حال حاضر یکی از معتبرترین شرکت های حوزه IT و تجهیزات الکترونیکی در ایران است. سه برند X.vision و  kisabz. و Sharp برای این شرکت می باشد.', 0, 1, 4, NULL, NULL),
(17, 5, 'خانه مستند انقلاب اسلامی', 'customers/images/17.png', 4, 'خانه مستند انقلاب اسلامی', 0, 1, 4, NULL, NULL),
(18, 18, 'صندوق ذخیره کارکنان بانک شهر', 'customers/images/18.png', 4, 'در خرداد 1395 تفکر افزایش سطح رفاه و معیشت پرسنل بانک شهر و شرکت های تابعه و افزایش قدرت خرید منجر به تاسیس صندوق ذخیره کارکنان بانک شهر.', 0, 1, 4, NULL, NULL),
(19, 19, 'فرهاد اکسچنج', 'customers/images/19.png', 4, 'صرافی فرهاد اکسچنج از بروزترین و کارآمدترین صرافی های تهران است معاملات خود را به صرافی فرهاد اکسچنج بسپارید.', 0, 1, 4, NULL, NULL),
(20, 20, 'داروخانه دکتر صوفی بروجردی', 'customers/images/20.png', 4, 'داروخانه دکتر بروجردی واقع در خیابان پیروزی خیابان نبرد پاسخگوی نیاز های شما به داروهای مختلف است.', 0, 1, 4, NULL, NULL),
(21, 21, 'فروشگاه موبایل آمیتیس سنتر', 'customers/images/21.png', 4, 'آمیتیس سنتر، با هدف فروش کالا های دیجیتال با قیمت های مناسب، واقع در شهر تهران، در خوزه خرید و فروش لوازم دیجیتالی (همچون موبایل، تبلت و...) فعالیت می کند.', 0, 1, 4, NULL, NULL),
(23, 9, 'کاخانجات سنگ ایران مرمر', 'customers/images/23.png', 4, 'کارخانه سنگ ایران مرمر:\nسال تولید: ۱۳۴۴ \nموسس: حسن زنگنه\nفن آوری پیشرفته، خلاقیت، و کیفیت در پردازش سنگ طبیعی مورد توجه اصلی ایران مرمر است.', 0, 1, 4, NULL, NULL),
(24, 24, 'کانون فرهنگی هم عهد', 'customers/images/24.png', 4, 'وب سایت هم عهد وب سایت رسمی استاد محمد باقر حیدری کاشانی می باشد که فعالیت ان در زمینه مهدویت و سبک زندگی مهدوی است.', 0, 1, 4, NULL, NULL),
(25, 15, 'کارخانجات واکس بلدرچین', 'customers/images/25.png', 4, 'واکس بلدرچین بزرگترین تولید کننده انواع واکس در ایران و صادرکننده به بازارهای بین المللی است.', 0, 1, 4, NULL, NULL),
(26, 26, 'تیم بسکتبال پارسیا', 'customers/images/26.png', 4, 'تیم بسکتبال پارسیا ویژه نونهالان و نوجوانان در لیگ بسکتبال ایران است.', 0, 1, 4, NULL, NULL),
(27, 27, 'بابا محمود', 'customers/images/27.png', 4, NULL, 0, 1, 4, NULL, NULL),
(28, 28, 'کارخانه سنگ مهساب سنگ', 'customers/images/28.png', 4, 'با قدمتی ده ساله و متکی بر تجربه و دانش و مهارت به روز دنیا علم و هنر را برای بدست آوردن بهترین نتیجه در زمینه تولید سنگ به خدمت گرفته است.', 0, 1, 4, NULL, NULL),
(29, 29, 'انوشه', 'customers/images/29.png', 4, NULL, 0, 1, 4, NULL, NULL),
(30, 30, 'شرکت آسا تجارت', 'customers/images/30.png', 4, 'شرکت آسا تجارت در زمینه صادرات کالا و خدمات در عرصه های بین المللی فعالیت می کند.', 0, 1, 4, NULL, NULL),
(31, 31, 'word of tile', 'customers/images/31.png', 4, NULL, 0, 1, 4, NULL, NULL),
(32, 32, 'عکس مستند', 'customers/images/32.png', 4, 'جشنواره عکس مستند هر ساله برای شناسایی افراد با استعداد در زمینه عکاسی مستند برگزار می شود.', 0, 1, 4, NULL, NULL),
(33, 33, 'سمعک برتر', 'customers/images/33.png', 4, 'کلینیک تخصصی شنوایی سمعک برتر، نمایندگی فروش انواع سمعک ها با قیمت های مناسب هستند که واقع در خیابان شریعتی  فعالیت می کنند.', 0, 1, 4, NULL, NULL),
(34, 34, 'ماشین آلات اداری اسماری', 'customers/images/34.png', 4, 'ماشین آلات اداری آسماری عرضه کننده ماشین آلات اداری از جمله دستگاه کپی و پرینتر است.', 0, 1, 4, NULL, NULL),
(35, 35, 'شرکت تبلیغاتی تب کار', 'customers/images/35.png', 4, 'شرکت تب کار وابسته به شرکت رسام اندیشه پارس شرکتی تبلیغاتی است جهت تبلیغات بر روی بدنه ی تاکسی ها و خودرو های عمومی', 0, 1, 4, NULL, NULL),
(38, 38, 'فروشگاه اینترنتی مد و لباس محرر', 'customers/images/38.png', 4, 'با هدف تولید ملی در صنعت پوشاک و مد اسلامی فعالیت می کند.', 0, 1, 4, NULL, NULL),
(39, 39, 'بنیاد کارآفرینی غدیر', 'customers/images/39.png', 4, 'مجمع کارآفرینان غدیر', 0, 1, 4, NULL, NULL),
(40, 4, 'محصولات غذایی رنیس', 'customers/images/40.png', 4, 'کارخانه محصولات غذایی رنیس با تکیه برتوان داخل به تولید انواع ادویه جات میپردازد.', 0, 1, 4, NULL, NULL),
(41, 41, 'هیبا', 'customers/images/74e08bde504cb6da7f9901582e6ec6d242.png', 4, 'فروشگاه مد و لباس هیبا در زمینه پوشاک اسلامی و حجاب استایل به صورت آنلاین در اینستاگرام و بصورت حضوری در پاساژ گالریا محصولات خود را به فروش میرساند', 0, 1, 4, NULL, NULL),
(42, 42, 'آژانس مهاجرتی آوانا', 'customers/images/1475741751755c78d0b29dd771d4a5ec37.png', 4, 'آژانس مهاجرتی آوانا', 0, 1, 4, NULL, NULL),
(43, 16, 'جیلی', 'customers/images/85b5333a16357d6c45a7023b901ca4a422.png', 4, 'یکی از شرکت های خصوصی خودرو سازی چینی است.', 0, 1, 4, NULL, NULL),
(44, 44, 'لیلیا', 'customers/images/969f5178bbb64a058acfdb9ec9438532lilia5.png', 4, 'لیلیا', 0, 1, 4, NULL, NULL),
(45, 45, 'سنگ کیوان', 'customers/images/fea85870962c4426f2a404c01a6bc84dlogo-dark-1.png', 4, 'مجموعه سنگ کیوان در سال ۱۳۸۴ فعالیت خود را آغاز کرد. این مجموعه ابتدا در زمینه واردات سنگ های خارجی فعالیت داشت اما با توجه به پیشرفتی که طی سال های اخیر داشت، در حال راه اندازی بخش تولیدی سنگ های زیبای ایرانی است.', 0, 1, 4, NULL, NULL),
(46, 11, 'کانون پرورش فکری کودکان و نوجوانان', 'customers/images/TEQcdTXeJIaqQf80TmrGaD8fEjVjlD.png', 4, '<p>کانون پرورش فکری کودکان و نوجوانان</p>', 0, 1, 4, NULL, '2023-07-05 13:28:52'),
(47, 8, 'اتوکالا', 'customers/images/V7B954MP1dOiSbxMJBMYx32GoEohwF.png', 4, '<p>وب سایت اتوکالا سایتی بسیار گسترده و جامع در صنف لوازم یدکی خودروهای سبک و سنگین ،ماشین آلات راهسازی و کشاورزی &nbsp;و تعمیرگاه هاست که فروشگاه های لوازم یدکی خودرو و تعمیرگاه ها را در شهر و استان شما به نمایش در می آورد</p>', 0, 1, 4, '2023-06-05 04:09:16', '2023-06-05 04:09:16'),
(171, 6, 'بنیاد مستضعفان انقلاب اسلامی', 'customers/images/0dsYkqCzqxW2yYVIBbhtXIMixa4dTc.png', 4, '<p>بنیاد مستضعفان انقلاب اسلامی</p>', 0, 0, 1, '2023-06-25 15:07:45', '2023-06-25 15:07:45'),
(172, 172, 'گروه خیرین عمار', 'customers/images/zZnUJ6FHllbcD1gmQF8QUOpf5OOCAI.png', 4, '<p>گروه خیرین عمار</p>', 0, 0, 1, '2023-06-25 15:08:43', '2023-06-25 15:08:43'),
(173, 3, 'پوشک هانیز', 'customers/images/5i3RtsJ8qUjXO79rAYGYsuBrBXXO0H.png', 4, '<p>پوشک هانیز</p>', 0, 0, 1, '2023-06-25 15:09:29', '2023-06-25 15:09:29'),
(174, 174, 'حیدری کاشانی', 'customers/images/QKLJNuglJqlpxxJUJKQzHI3AfjoYnm.png', 4, '<p>حیدری کاشانی</p>', 0, 0, 1, '2023-06-25 15:09:57', '2023-06-25 15:09:57'),
(175, 175, 'بوستان فنآوری و نوآوری گلستان', 'customers/images/B7blWsgS7IsrDJxS1AMUHUKbdfWk0l.png', 4, '<p>بوستان فنآوری و نوآوری گلستان</p>', 0, 0, 1, '2023-06-25 15:10:28', '2023-06-25 15:10:28'),
(176, 176, 'نوین سازه هراز', 'customers/images/n9zZ87m1jDgcdWB6SPktwDZBEwMOSn.png', 4, '<p>نوین سازه هراز</p>', 0, 0, 1, '2023-06-25 15:11:53', '2023-06-25 15:11:53'),
(177, 14, 'گروه پرستیژ', 'customers/images/uivz4p3RI8xjScmftDPBAWOEJSniIt.png', 4, '<p>گروه پرستیژ</p>', 0, 0, 1, '2023-06-25 15:12:18', '2023-06-25 15:12:18'),
(178, 178, 'توسعه دانش بنیان سینا', 'customers/images/XIF9JT4KfTemi3Dpc8rxosO0di8UcL.png', 4, '<p>توسعه دانش بنیان سینا</p>', 0, 0, 1, '2023-06-25 15:12:51', '2023-06-25 15:12:51'),
(179, 18, 'ادویه و خشکار دلیمان', 'customers/images/l7yUL1B7KnuuGaPMLdC2KCYiHu9ZTh.png', 4, NULL, 0, 0, 1, '2023-06-30 09:39:18', '2023-06-30 09:39:18');

