(210, 'ساخت موشن گرافیک', '<p>طراحی و ساخت لوگوموشن صامه دیزاین</p>', 48, NULL, 'portfolios/images/seayhEpxtPUcSm4DO5nQ7niLds93mH.jpg', 'portfolios/videos/719QPxbsYSpvw0tPMdJsuQvAu8Zqtm.mp4', 'ساخت-موشن-گرافیک-3', NULL, NULL, 'ساخت موشن گرافیک، لوگوموشن', 4, 5, 39, 0, NULL, '2021-02-08 08:09:48', '2023-06-29 14:12:01'),
(213, 'لوگو موشن کارخانه سنگ دومارین', '<p>لوگو موشن کارخانه سنگ دومارین</p>', NULL, 5, 'portfolios/images/BuWCggHVfTzt2KWxAFZrt3movUosBn.jpg', 'portfolios/videos/4TrC3RpHPKsMOspSNRaBatlI3Cbm3S.mp4', 'لوگو-موشن-کارخانه-سنگ-دومارین', NULL, NULL, NULL, 4, 5, 66, 0, 1, '2023-06-30 05:43:36', '2023-06-30 06:07:06'),
(214, 'لوگو موشن هانیز', '<p>طراحی لوگو موشن پوشک هانیز</p>', NULL, 173, 'portfolios/images/GaTR4oYQbc9yFKeu9AXNAFDASy6UUS.jpg', NULL, 'لوگو-موشن-هانیز', NULL, NULL, 'لوگو موشن هانیز', 4, 5, 22, 0, 1, '2023-06-30 06:50:07', '2023-06-30 06:50:07'),
(217, 'فروشگاه اینترنتی مدو لباس محرر', '<p>فروشگاه اینترنتی مدو لباس محرر</p>', NULL, 38, 'portfolios/images/BhDP2dFxNJbakOxeBHP55ldzbjhz5k.jpg', 'portfolios/videos/IUzbQWsnGM1n8qPmmfPrglc9P0XL6a.mp4', 'فروشگاه-اینترنتی-مدو-لباس-محرر', NULL, NULL, 'فروشگاه اینترنتی مدو لباس محرر', 4, 5, 22, 0, 1, '2023-06-30 09:09:06', '2023-06-30 09:10:58'),
(218, 'ادویه وخشکبار دلیمان', '<p>ادویه وخشکبار دلیمان</p>', NULL, 179, 'portfolios/images/FgIonJWttVv6S3k5FbDJdEJxKrsJdj.jpg', NULL, 'ادویه-وخشکبار-دلیمان', NULL, NULL, 'ادویه وخشکبار دلیمان', 4, 6, 28, 0, 1, '2023-06-30 09:44:08', '2023-07-01 11:08:31'),
