INSERT INTO `submenus` (`id`, `menu_id`, `title`, `slug`, `class`, `icon`, `route_active`, `description`, `text`, `images`, `count`, `status`, `keycheck`, `service_id`, `tags`, `footer_show`, `user_id`, `created_at`, `updated_at`) VALUES
(1, 3, 'سئو وب سایت', 'سئو-وب-سایت', NULL, NULL, 1, 'به مجموعه عملیات  فنی که بر روی وبسایت جهت قرار گرفتن آن جز اولین سایت های نتیجه جستجو گوگل انجام می گیرد و باعث افزایش بازدید کنندگان و جذب  مشتریان به وب سایت شما می شود را، سئو(SEO)میگوییم.', 'کلمه SEO تشکیل شده از search engine optimization به معنی بهینه سازی موتورهای جستجوگر می باشد.\r\n\r\nاگر دقت کرده باشید اکثر کاربرانی که در موتور جستجوگر گوگل موضوعی را جستجو میکنند، فقط سایت هایی که در صفحه اول گوگل هستند را کلیک میکنند و حتی بیشتر مواقع به ۳ الی ۴ سایت اول صفحه نخست بسنده میکنند که همین امر نشان دهنده اهمیت زیاد سئو وب سایت است.\r\nشما با اطمینان به متخصص طراحی وب سایت با خیالی آسوده میتوانید سئو سایت خود را افزایش داده، در نتیجه شاهد افزایش رتبه سایت و همچنین موفقیت های کسب و کارتان باشید.\r\nرعایت قوانین موتور جستجوگر گوگل در کنار جذب رضایت مشتری \r\nاز بهترین روش ها برای افزایش سئو وب سایت است، که به آن سئو کلاه سفید میگوییم.\r\nبرای دستیابی به بهترین سئو وب سایت نیازمند سه اهرم هستیم:\r\nSEO one page (سئو داخلی) ، SEO off page (سئو خارجی) ، SEO technical (سئو فنی)', 'images/submenu/2020/e01335af7e4d6b27d0cfb112a2201b6cwww-animate.png', 1, 4, NULL, 7, 'سئو وب سایت ، بهینه سازی وبسایت ، طراحی وب سایت بهینه ، کلمات کلید ، موتور جستجوی گوگل ، وبسایت سئو شده ، آژانس تبلیغاتی', 1, 1, NULL, '2020-10-25 15:20:13'),
(2, 3, 'تبلیغات اینترنتی', 'تبلیغات-اینترنتی', NULL, NULL, 1, 'برای انتشار تبلیغات در جهان دیجیتال باید به اینترنت متصل باشیم، به این قبیل از تبلیغات، تبلیغات اینترنتی یا آنلاین گفته می شود. افزایش آگاهی مشتریان نسبت به نام تجاری و بهبود کسب و کار مهم ترین هدف تبلیغاتی اینترنتی است.', 'فواید تبلیعات اینترنتی بیشتر از تبلیغات سنتی است، زیرا:\r\n◇در تبلیغات اینترنتی ارتباطمان با مخاطبان آسان تر،سریع تر و بهتر است.\r\n◇علایق و سلایق مخاطبان را دقیق تر شناسایی کرده و طبق آنها محتوای تبلیغات را میسازیم.\r\n◇هزینه کاهش پیدا میکند.\r\nتبلیغات اینترنتی انواعی دارد:\r\n○تبلیعات همسان(NATIVE):\r\n در این روش ما تلاش میکنیم راه حل هایی برای مشکلات کاربران ارائه دهیم تا اعتماد و نظر آنها را نسبت به خود(برندمان)جلب کنیم. ساده ترین ابزار برای این نوع تبلیغات وبلاگ است.\r\n○تبلیعات از طریق موتورهای جستجو(SEM):\r\nافزایش آگاهی کاربران از نام تجاری در مدت زمان کوتاه،هدف ما \r\nدر این روش از تبلیعات اینترنتی است. در این روش برای نمایش دادن تبلیغات از کلمات مهم و کلیدی مربوط به کسب و کارتان استفاده میشود. برای معرفی بهترین ابزار این روش میتوانیم به گوگل ادورز (Google adwords) اشاره کنیم.\r\n○تبلیغات تلفن همراه:\r\nبه دلیل اینکه امروزه استفاده از تلفن همراه بسیار رایج شده است \r\nسرمایه گذاری برای تبلیغات موبایل بسیار مفید است.\r\nبا استفاده از این روش نه تنها میتوانید دسترسی بهتری به کاربرانتان داشته باشید بلکه سئو سایت شما بهبود میابد.\r\n○تبلیغات در فضای مجازی:\r\nاین روش برای دسترسی به مشتریان جدید بسیار موثر است زیرا در این عصر نوین قسمت بزرگی از زندگی ما صرف فضای مجازی میشود. تبلیغات در فیسبوک، اینستاگرام، یوتیوب، توییتر و... انواع تبلیغات در فضای مجازی است.\r\n○تبلیغات ویدیویی: \r\nاین روش طرفداران بسیاری دارند و همچنین تاثیر خوبی بر روی کسب و کار شما. شما میتوانید با قرار دادن ویدیوهای تبلیغاتی خود در بزرگترین پایگاه ویدیویی جهان \"یوتیوب\" و دیگر سایت های مخصوص کسب و کارتان را رو به جلو هل دهید.\r\n○تبلیعات مارکتینگ: \r\nدر این روش تبلیغات شما برای کسانی به نمایش در می آید که قبلا از وب سایتتان بازدید کرده باشند.\r\n○ایمیل مارکتینگ:\r\nبا نگهداری مشتری های فعلی بسیار هزینه کمتری نسبت به جذب مشتری جدید میپردازید، ایمیل مارکتینگ به خوبی در اجرا کردن این اصل به شما کمک خواهد کرد.', 'images/submenu/2020/d57ffe05016a0db04a1221863adb8403website-setup-animate.png', 0, 4, NULL, 8, 'تبلیغات اینترنتی ، تبلیغ بر بستر اینترنت ، ریپورتاژ ، آگهی تبلیغاتی ، تبلیغات حرفه ای ، تبلیغات ارزان ، تبلیغات خفن ، تبلیغ رایگان ، اژانس تبلیغاتی', 0, 1, NULL, '2020-12-01 07:03:25'),
(6, 3, 'گوگل ادز', 'گوگل-ادز', NULL, NULL, 1, 'در قبال هزینه ای که به گوگل ادز(ادوردز) پرداخت میکنید، با استفاده از کلمات کلیدی، وب سایت شما در صفحه اول گوگل و بالاتر از سایت های دیگر به نمایش گذاشته می شود‌. امروزه به دلیل موثر بودن این کار، رقابت در گوگل ادز بسیار تنگاتنگ است.', '◇چگونه از گوگل ادوردز استفاده کنیم؟\r\nدر قبال هزینه ای که به گوگل ادوردز پرداخت میکنید، با استفاده از کلمات کلیدی، وب سایت شما در صفحه اول گوگل و بالاتر از سایت های دیگر به نمایش گذاشته می شود‌.\r\n◇هزینه های گوگل ادوردز بر چه اساسی محاسبه میشود؟\r\nزمانی که گوگل ادوردز با استفاده از کلمات کلیدی وب سایت شما را بالاتر از سایت های دیگر آورد، به ازای هر کلید کاربران روی سایت شما، مبلغی از حسابی که در گوگل ادوردز ایجاد کرده اید کم میشود(PPC)، و این نه تنها برای شما ضرر ندارد، بلکه باعث افزایش رتبه سایتتان شده و در نتیجه سایت شما همیشه صدر صفحه اول گوگل دیده میشود.\r\n○مطمئن باشید که آژانس تخصصی تبلیعاتی بستا این اتفاق را توسط متخصصین خود در این حوزه به خوبی برای شما رقم خواهد زد.', 'images/submenu/2020/e26c0b825d645206125dcee6c7c1b6aaگوگل ادز.png', 0, 4, NULL, 12, NULL, 0, 1, NULL, '2020-12-01 07:07:28'),
(7, 3, 'تولید محتوای اختصاصی', 'تولید-محتوای-اختصاصی', NULL, NULL, 1, 'هنگامی که برای وب سایت،پیج اینستاگرام،کانال تلگرام و... یک محتوای کاملا نو را ایجاد می‌کنید،محتوایی مخصوص به خود را به اشتراک گذاشته اید. این کار باعث جذب بسیاری از مخاطبان و همچنین جلب توجه گوگل و دیگر شبکه های اجتماعی میشود.', 'تولید صفر تا صد یک محتوای درجه یک و پر کشش شاید به ساعت ها تفکر و مطالعه نیاز داشته باشد. به همین دلیل است که کار پر درآمد و ارزشمندی است.\r\nرعایت نکات زیر به شما برای تولید محتوای اختصاصی کمک می‌کند:\r\n-قسمت هایی از محتوا را از آموزه ها و تجربیات خود بنویسید.\r\n-از مقاله های انگلیسی کمک بگیرید، آنها را ترجمه کنید.\r\n-محتوا را بر اساس یک علاقه مشترک بین اکثر مخاطبان بنویسید.\r\n-در محتوای خود احساس صمیمیت را به مخاطب منتقل کنید.\r\nشما در گام اول باید محتوا را به گونه ای بسازید که مخاطب با مطالعه ۵الی۶خط آن کسل نشود و از سایت یا پیج شما خارج نشود.', 'images/submenu/2020/274d2fcf63b3ce1a9230859c4638aea7working-remotely-animate.png', 0, 4, NULL, 13, NULL, 1, 1, NULL, '2020-10-25 15:22:14'),
(8, 3, 'مدیریت شبکه های اجتماعی', 'مدیریت-شبکه-های-اجتماعی', NULL, NULL, 1, 'امروزه به دلیل بالا رفتن سطح سواد و آگاهی مردم،نمیتوان از محتوا های پوچالی و یا بسیار ابتدایی برای اشتراک گذاری در شبکه های اجتماعی استفاده کرد. شبکه های اجتماعی یکی از مهم ترین شاخه های بازاریابی دیجیتال است که مدیریت صحیح آن بسیار در جذب مشتری موثر است.', 'در دنیای امروز اکثر کسب و کارها قسمتی از زمان و بودجه خود را به بازاریابی دیجیتال و یا مدیریت شبکه های اجتماعی اختصاص می‌دهند. این اتفاق نشان دهنده بازخورد بسیار خوبی از طرف شبکه های اجتماعی است.\r\nدیگر نمی‌توانیم کسب و کارهای‌زیادی را پیدا کنیم که از روش های سنتی و قدیمی برای رونق بازار کارشان استفاده می‌کنند.\r\nاما باید این نکته را در نظر داشت که مدیریت شبکه های اجتماعی کار آسانی نیست،برای نتیجه گرفتن از فضای مجازی یا شبکه های اجتماعی باید نکاتی کلیدی و قابل توجهی را دانست و  رعایت کرد.\r\nبرخی از این نکات را می‌توان این گونه مطرح نمود:\r\n○ابتدا اهدافتان را تعیین‌ کنید.\r\n○سپس مخاطبان خود را شناسایی کنید.\r\n○برای ساختن محتوا ها و انتخاب تصاویر دقت زیادی به خرج دهید\r\n○بعد از اشتراک گذاری محتوای خود نتایج و بازخورد ها را بررسی کنید،به مرور زمان با سلایق مخاطبان آشنا می‌شوید و بر همان اساس محتوا سازی میکنید.\r\n○کارآمد ترین شبکه اجتماعی را انتخاب کنید و در آن فعالیت بیشتری داشته باشید.\r\n○برای مدیریت صفحه هایتان در شبکه های اجتماعی به متخصصین این حوزه اعتماد کنید.\r\nاین نکته را همیشه به یاد داشته باشید که اصلی ترین رکن در مدیریت شبکه های اجتماعی محتوا است.', 'images/submenu/2020/df5b84c140c2af5aec37b7dedd1ca9e6instagram-video-streaming-animate.png', 0, 4, NULL, 14, NULL, 1, 1, NULL, '2020-10-25 15:23:26'),
(9, 3, 'ایمیل مارکتینگ', 'ایمیل-مارکتینگ', NULL, NULL, 1, 'در حال حاضر بیش از ۳میلیارد نفر در جهان در حال استفاده از ایمیل هستند،همچنین صاحبان کسب و کار های اینترنتی ایمیل مارکتینگ را از مهم ترین ابزار کار خود میدانند. ایمیل مارکتینگ به همین سادگی که فکر میکنید نیست!برای آشنایی بیشتر با دنیای کاربردی و نکته سنج ایمیل مارکتینگ با ما همراه باشید.', 'حفظ و نگهداری از مشتریان قدیمی و برگرداندن آن ها به چرخه درآمدی از مهم ترین وظیفه های ایمیل مارکتینگ هست. ارسال ایمیل ها با حجم بالا و به هر شخصی ما را به مقصودمان نمیرساند بلکه ایمیل ها باید هدفمند طراحی و ارسال شوند تا بتوانیم در جذب هر چه بیشتر مشتری موفق باشیم در طراحی ایمیل باید دقت داشت که یک متن بسیار ساده نباشد چون مخاطب از دیدن آن خسته میشود بلکه باید از عکس هایی که تشکیل شده از رنگ بندی های شاد و مناسب با لوگوی شماست استفاده کرد به گونه ای که مخاطب جذب آن شود و همچنین متن تبلیغ مفید و مختصر باشد.', 'images/submenu/2020/680506bb0b02cb94a0533b8d6afea11femail.png', 0, 4, NULL, 15, NULL, 0, 1, NULL, '2020-10-25 15:21:28'),
(10, 3, 'طراحی اپلیکیشن اندروید', 'طراحی-اپلیکیشن-اندروید', NULL, NULL, 1, 'طراحی اپلیکیشن اندروید به کسب و کار شما کمک می کند، زیرا برنامه شما همیشه بر روی تلفن همراه مشتری نصب شده است و وی به راحتی می بتواند به شما دسترسی داشته باشد و از خدمات اپلیکیشن استفاده کند.', 'امروزه با دنیایی از برنامه های قابل نصب در گوشی های هوشمند با کاربری های مختلف  روبرو هستیم.\r\nاپلیکیشن ها به نرم افزارهایی در موضوعات مختلف (برای تلفن های همراه هوشمند و تبلت)  مثل ارائه خدمات، سرگرمی، آموزشی، ارائه اطلاعات و... طراحی و ساخته میشوند و بسیار کاربردی هستند.\r\nطراحی یک اپلیکیشن اندروید برای کسب و کار امکان سهولت در ارتباط با مشتری را فراهم میکند و باعث رونق گرفتن کسب و کار شما خواهد شد.\r\nبه شرط این که در طراحی اپلیکیشن اندروید به نکات زیر نهایت توجه را داشته باشید:\r\n۱- رنگ بندی مناسب\r\n۲- فونت خوانا و خوش رنگ\r\n۳- گرافیک مطلوب و با کیفیت\r\n۴- زبان برنامه نویسی مناسب', 'images/submenu/2020/eb6ee204ec363ad960f3bf87865083d9طراحی اپلیکیشن اندروید.png', 0, 4, NULL, 16, NULL, 0, 1, NULL, '2020-12-01 07:03:59'),
(11, 3, 'طراحی داشبورد مدیریتی', 'طراحی-داشبورد-مدیریتی', NULL, NULL, 1, 'داشتن یک داشبورد مدیریتی خوب به شما در مدیریت وب سایت و حتی کسب و کار شما بسیار کمک می کند و باعث می شود بتوانید مشتریان خود را به طور صحیح و یکپارچه مدیریت کنید.', 'با داشتن یک داشبورد مدیریتی اختصاصی می توانید به راحتی در هر زمان المان های سایت خود را تغییر داده و اطلاعات دقیقی از وب سایت داشته باشید.\r\nطراحی داشبورد مدیریتی تنها توسط متخصصین مربوطه می تواند به خوبی انجام گیرد و نسبت به مدیریت وب سایت مهم تر و  دشوار تر است.\r\nاگر برای سایت شما یک داشبورد عالی طراحی شود، شما می توانید به اطلاعات درون سایت به راحتی دسترسی داشته باشید، \r\nبه راحتی آن را درک کنید و تغییرات دلخواه خود را بر روی قالب سایت انجام دهید.\r\nبرای گرفتن اطلاعات بیشتر و تخصصی تر با مشاورین آژانس تخصصی بستا تماس حاصل فرمایید.', 'images/submenu/2020/81484cd227b1bf6dd4b7fa5c5e971f03طراحی داشبورد مدیریتی.png', 0, 4, NULL, 17, NULL, 1, 1, NULL, '2020-11-01 12:44:58'),
(12, 3, 'طراحی وب سایت', 'طراحی-وب-سایت', NULL, NULL, 1, 'امروزه هر کسب و کاری باید یک وب سایت خوب داشته باشد وب سایت خوب به شما کمک می کند خدمات، محصولات و برند شما به خوبی معرفی شود و به درستی و بدون محدودیت ارائه شود.', 'اگر بخواهیم یک وب سایت خوب را در موتور های جستجوگر به نمایش بگذاریم باید از دو زاویه متفاوت به آن نگاه کنیم.\r\nزاویه دید برنامه نویس سایت: \r\nبرنامه نویس پایه های یک وب سایت را بنا می کند، کد هایی می نویسد که تمام خواسته های شما را از یک وب سایت خوب تامین می کند.\r\nکار او هیچوقت توسط کاربر دیده نمی شود، در واقع پشت پرده یک وب سایت فوق العاده، برنامه نویس حرفه ای نشسته است.\r\nزاویه دید طراح سایت: \r\nطراح سایت می تواند آنقدر سایت شما را زیبا و تاثیر گذار طراحی کند، که هر‌ نوع کاربر را جذب خود کند.\r\nبه عبارتی طراحی وب سایت یعنی شکل و قالب سایت یا صفحه ای که برای کاربران به نمایش گذاشته می شود.', 'images/submenu/2020/3a1ea28463a965bf404953860648e2fbsitedesign.png', 0, 4, NULL, 18, NULL, 1, 1, NULL, '2020-10-25 14:54:30'),
(13, 4, 'طراحی کاتالوگ', 'طراحی-کاتالوگ', NULL, NULL, 1, 'مجموعه ای از اطلاعات دقیق و جامع درباره برند یا کسب و کاری را کاتالوگ میگوییم.\r\nکاتالوگ تنها به معنی نوشته هایی درباره یک مجموعه نیست،بلکه مهم ترین اصل در موثر بودن کاتالوگ،طراحی منحصربه فرد آن است.', 'مجموعه ای از اطلاعات دقیق و جامع درباره برند یا کسب و کاری را کاتالوگ میگوییم.\r\nکاتالوگ تنها به معنی نوشته هایی درباره یک مجموعه نیست،بلکه مهم ترین اصل در موثر بودن کاتالوگ،طراحی منحصربه فرد آن است.\r\nچرا در کاتالوگ اطلاعات با تمام جزئیات طراحی می‌شود؟\r\nزیرا کاتالوگ مستقیماً از تولید کننده به مصرف کننده منتقل نمی‌شود و تنها برای واسطه ها(یعنی ارائه دهنده های خدمات و یا فروشندگان محصولات)طراحی میشود.\r\nیک کاتالوگ جداب باید ویژگی های زیر را در خود جای دهد:\r\n◇با مخاطب صادق باشد،بیش از حد اقراق نکند.\r\n◇به گونه ای طراحی شود که تا حد امکان به سوالات پیش بینی شده مخاطب پاسخ دهد.\r\n◇تصاویر زیبا و مرتبط با متن داشته باشد.\r\n◇مخاطب برای مطالعه آن زمان زیادی را صرف نکند.\r\n◇اطلاعات در عین کامل بودن،مختصر و مفید باشد.\r\nو در آخر اگر یک مجموعه کاتالوگ جذابی طراحی کرده باشد،مخاطب برای به دست آوردن اطلاعات درباره آن مجموعه قبل از اینترنت، به سراغ کاتالوگ آن می‌رود، و تا آخرین صفحه آن را مطالعه میکند.', 'images/submenu/2020/ec018197af1cd92c2e6af069ff856859طراحی کاتالوگ.png', 0, 4, NULL, 19, NULL, 1, 1, NULL, '2020-11-07 05:15:50'),
(14, 4, 'طراحی لوگو', 'طراحی-لوگو', NULL, NULL, 1, 'طراحی یک لوگو منحصر به فرد، هویت بصری برند، محصولات یا خدمات شما را در چشم مشتری قوی تر و موثر تر نشان می دهد.\r\nاولین مشخصه ای که از کسب و کارتان در ذهن مشتری می‌ ماند لوگو مجموعه شماست.', 'لوگو نمادّی از کسب و کار شماست. با یک طراحی زیبا و هیجان انگیز رو به رو هستیم‌. \r\nلوگو،معرف هویت یک مجموعه است، به همین علت است که طراحی آن از اهمیت بسیار بالایی برخوردار است. \r\nشما بدون داشتن یک نام و آرم تجاری نمی‌توانید به خوبی در بازار کسب و کار رقابت کنید.\r\nلوگو یا آرم یک مجموعه باید به گونه ای طراحی شود که هم سادگی خود را حفظ کند،هم بیانگر هویت آن مجموعه باشد و مهم ترین اصل این است که در ذهن مخاطب ماندگار باشد.\r\nمی توانید با اطمینان به یک آژانس تخصصی تبلیغات و با خیالی آسوده از هنر طراحان گرافیک آژانس،یک لوگوی زیبا و مناسب با هویت و پرستیژ کاری خود تحویل بگیرید.', 'images/submenu/2020/a1905917fac426ecdf630248e1d7ebb7logo.png', 0, 4, NULL, 20, NULL, 1, 1, NULL, '2020-10-25 14:59:00'),
(15, 4, 'طراحی ست اداری', 'طراحی-ست-اداری', NULL, NULL, 1, 'ست اداری یکی از ملزومات پرطرفداری است که وجودش در هر شرکت و کسب و کاری الزامی است. ست اداری تشکیل شده از سربرگ ها، پاکت های نامه و... که کاربرد بسیاری  در محل کار دارد .', 'ست اداری مجموعه ای از ابزاری پر کاربرد از قبیل گیره، زونکن، فنر پوشه، لوازم التحریر، منگنه، تراکت، بروشور،لیبل، تقویم رو میزی و... که بر روی تمامی آنها طرحی از نام و لوگوی شرکت چاپ میشود،است.\r\nدر اصل استفاده از ست اداری به شرکت شما نظم خاصی می‌دهد.\r\nهم محیط اداره را زیبا تر و مرتب می‌سازد و هم باعث عدم کپی برداری از مکتوبات، طراحی ها و دیگر کار هایی که بر روی ابزار ست اداری انجام می‌دهید می‌شود.', 'images/submenu/2020/13add93c79c212a5528d81bf46de7318طراحی ست اداری.png', 0, 4, NULL, 21, NULL, 1, 1, NULL, '2020-11-01 12:47:09'),
(16, 4, 'تولید انیمیشن', 'تولید-انیمیشن', NULL, NULL, 1, 'به زبان ساده توانایی جان بخشیدن و حرکت به تصاویر توسط نرم افزار های انیمشین سازی را تولید انیمیشن میگویند. یک انیمیشن خوب با قدرت تاثیر گذاری خود همیشه هویت شما را در ذهن مشتری حفظ می کند.', 'امروزه نرم افزارهای زیادی در حوزه ی انیمیشن و فیلم سازی در بازار موجود است از photoshop تا aftereffects که هر کدام \r\nویژگی های خاص خود را دارند...\r\n\r\nبه زبان ساده توانایی جان بخشیدن و حرکت به تصاویر توسط نرم افزار های انیمشین سازی را تولید انیمیشن میگویند...\r\nاین حرفه به این سادگی ها هم که فکرش را میکنید نیست حرفه پیچیده و زمان بری است ولی یکی از پردرآمد ترین حرفه هاست مخصوصا در حوزه تبلیغات...\r\nبهترین انیمیشن سازان دنیا: \"waltdisni\" و \"pixar\" \r\nالمان های مهم در تولید یک انیمیشن حرفه ای:\r\n○دارای محتوای پر مغز\r\n○خلاق بودن به کارگیری ایده های نو\r\n○مسلط بودن به نرم افزار های مرتبط با این حرفه\r\n○کمک گرفتن از صدا گذاران حرفه ای\r\n○ایجاد ارتباط درست بین صدا و تصویر\r\n○صرفه جویی در زمان،هنگامی که انیمیشن ما در حوزه تبلیغات و آگهی باشد.', 'images/submenu/2020/df46033fe81493c3e1db3016e33bb7adanimation.png', 0, 4, NULL, 22, NULL, 1, 1, NULL, '2020-10-25 14:59:24'),
(17, 4, 'طراحی بسته بندی', 'طراحی-بسته-بندی', NULL, NULL, 1, 'سته بندی ظرفی است که از سلامت محصول در فاصله تولید تا مصرف محافظت می‌کند، و همچنین طراحی مناسب بسته بندی ،محصول را شکیل تر و جذاب تر می‌کند و مخاطب را ترقیب به خرید میکند.', 'بسته بندی محصولات باعث حفظ سلامت کالا می‌شود، ارتباط تولید کننده را با مصرف کننده افزایش می‌بخشد.\r\nهنگامی که یک محصول به دست مشتری می‌رسد اولین مشخصه ای که مشتری می‌تواند با آن ارتباط بر قرار کند و ممکن است نظرش را جلب کند،بسته بندی و طراحی آن است.\r\nما امروزه در هر جا و هر تجارتی با انبوه بسته بندی های مختلف رو به رو می‌شویم، و صاحب کسب و کاری در این رقابت برنده است که با طراحی منحصربه فرد خود می‌تواند مشتری اش را هنگام دریافت کالا شگفت زده کند و تنها همان بسته بندی بتواند محصول و برند شما را در ذهن وی ماندگار سازد.\r\nهمانطور که گفتیم،مهم ترین بخش بسته بندی طراحی آن است،\r\nزیرا که در طراحی بسته بندی،طراحان به زوایای مهمی در طراحی،همچون علمی بودن،هنری بودن آن و... می‌پردازند.', 'images/submenu/2020/6049dcea0d3b645ddf2ae255bdfa3222طراحی بسته بندی.png', 0, 4, NULL, 23, NULL, 0, 1, NULL, '2020-11-01 12:41:17'),
(18, 4, 'طراحی بروشور', 'طراحی-بروشور', NULL, NULL, 1, 'یک بروشور حاوی مطالبی است که مخاطب را از نحوه استفاده از محصول آگاه می‌سازد. در ابتدا لازم به ذکر است که بروشور نسبت به ابزار های دیگر تبلیغات بسیار به صرفه و کم هزینه و در عین حال سودآور است.', 'بروشور معمولا برای اطلاع رسانی درباره یک موضوع طراحی و تولید می‌شود،مثل توصیه هایی درباره مسائل بهداشتی،آموزشی،نحوه استفاده از محصولات،تبلیغ محصولات و...\r\nدر قسمتی از بروشور به طور مختصر در مورد مزایای محصول و یا موضوع اطلاع رسانی نوشته می‌شود و در قسمت های دیگر توضیحات و تصاویر مرتبط، به نمایش گذاشته می‌شود.\r\nبرای دسترسی به یک بروشور تاثیر گذار باید نکات زیر رعایت شود:\r\n◇مطالب مفیدی با ادبیاتی جذاب در آن قرار دهید.\r\n◇از مطالبی استفاده کنید که به صورت صادقانه اعتبار کسب و کار شما را به مخاطب ثابت کند.\r\n◇مطالبی که،مخاطب را به انجام رفتاری خاص ترقیب کند.\r\n◇بروشور را به شکلی طراحی کنید که حمل و توزیع آن به آسانی شکل گیرد.', 'images/submenu/2020/0beab58cc06b1d9e5cab5b041ae9c902طراحی بروشور.png', 0, 4, NULL, 24, NULL, 0, 1, NULL, '2020-11-05 06:54:00'),
(19, 4, 'طراحی کارکتر', 'طراحی-کارکتر', NULL, NULL, 1, 'داشتن یک کارکتر از برند، محصولات و یا خدمات شما باعث می شود که هر جایی تنها کارکتر شما دیده شود تمام خدمات یا محصولات و کیفیت آنها در چشم مشتری تداعی شود. همچنین با طراحی کارکتر می توانید ارتباط بهتری با مخاطب داشته باشید.', 'طراحی کاراکتر تنها به منزله طراحی یک موجود نیست،بله باید بتوانیم یک شخصیت کامل را در ذهن خود زنده کرده و سپس تا حد امکان ویژگی هایش را درون طرح ظاهرش قرار دهیم، که این شخصیت ها تنها با تلاش و پشت کار به واقعیتی جذاب مبدل می‌گردد.\r\nبرای طراحی یک کاراکتر قوی و چشم گیر باید نکاتی رعایت شود:\r\n◇در ابتدای طراحی باید مخاطبان کاراکتر خود را شناسایی کنید و بر اساس سن و علایق آنها شخصیت را خلق کنید.\r\n◇همچنین مکان و رسانه ای ‌که قرار است شخصیت خیالی خود را در آنجا ارائه دهید در نظر داشته باشید.\r\n◇در انتخاب رنگ دقت بسیار بالایی داشته باشید.\r\n◇برخی از ویژگی های مورد نظر در کاراکتر را با اقراق بیشتری به نمایش بگذارید.\r\n◇مطالعه و جستجو را هیچوقت فراموش نکنید.\r\n◇یک کاراکتر منحصربه فرد خلق کنید تا از دیگر شخصیت های خیالی کاملا متمایز باشد.\r\n◇در طراحی کاراکتر خود،سادگی را حفظ کنید.', 'images/submenu/2020/5d4d03c88bb4fb391ef5bf91f04c338cchareckter.png', 0, 4, NULL, 25, NULL, 0, 1, NULL, '2020-10-25 14:59:44'),
(20, 4, 'طراحی اینفوگرافی', 'طراحی-اینفوگرافی', NULL, NULL, 1, 'اینفوگرافی به زبان ساده،تصویری است که مجموعه ای از اطلاعات را در خود قرار داده است. به عبارتی دیگر اطلاعاتی که یه صورت دیداری به نمایش گذاشته می‌شود را اینفوگرافی می‌گویند.', 'برخی از مزایای اینفوگرافی عبارتند از:\r\n○زمان کمتری برای مطالعه آن صرف می‌شود \r\n○به دلیل شکل تصویری اش،به فهم و در‌ک سریع تر،کمک می‌کند \r\n○با مطالعه یک اینفوگرافی میتوان اطلاعات را بهتر و روان تر به دیگران انتقال داد\r\nاینفوگرافی انواعی دارد:\r\n◇اینفوگرافی اطلاع رسان\r\n◇اینفوگرافی های نموداری\r\n◇اینفوگرافی فرایندی\r\n◇اینفوگرافی های آموزشی\r\n◇اینفوگرافی رزومه\r\n◇اینفوگرافی مقایسه ای\r\n◇اینفوگرافی عددی\r\nلازم به ذکر است که مهم ترین بخش اینفوگرافی طراحی آن است.\r\nرنگ ها،فونت ها،آیکن ها،تصاویر در یک اینفوگرافی موثر باید حرفه ای طراحی شوند تا مخاطب را جذب نوشته ها یا مطالبشان  کنند.', 'images/submenu/2020/506919462a0d2fec0cf313c8df67bdedطراحی اینفوگرافی.png', 0, 4, NULL, 26, NULL, 0, 1, NULL, '2020-11-05 06:40:42'),
(21, 4, 'طراحی کارت ویزیت', 'طراحی-کارت-ویزیت', NULL, NULL, 1, 'کارت ویزیت،کارت های زیبا و کوچکی که اطلاعات فردی و مشخصات یک برند را در خود جای می دهند و  بهترین،سریع ترین و آسان ترین تکنینک برای تبلیغ و برقراری ارتباط هرچه بیشتر با مشتری یکی از قابلیت های کارت ویزیت است.', 'کارت ویزیت به دلیل حجم کمی که دارد به راحتی قابل حمل است. اگر طراحی کارت ویزیت ماهرانه و هوشمندانه باشد\r\nهر کجا که کارت ویزیت خود را از جیب در آورده و به عنوان معرفی مختصری از کسب و کارتان به کسی دهید،امکان ندارد که فرد، لحظاتی به کارت شما خیره نشود و نام و آرم تجاری شما در ذهنش ماندگار نشود.\r\nنکات مهم برای طراحی یک کارت ویزیت تاثیر گذار:\r\n◇کارت ویزیت خوب در عین سادگی باید از رنگ های متنوع و متناسب با رنگ لوگو باشد.\r\n◇سایز لوگو بر روی کارت ویزیت استاندارد باشد.\r\n◇جنس کاغذ کارت ویزیت با کیفیت باشد.\r\n◇سایز کارت استاندارد باشد.\r\n◇برای متن روی کارت از فونت های زیبا و خوانا استفاده کنید.', 'images/submenu/2020/6ed75a52b33e140877dbd0462441ecacطراحی کارت ویزیت.png', 0, 4, NULL, 27, NULL, 0, 1, NULL, '2020-11-05 08:30:08'),
(22, 5, 'ساخت تیزر تبلیغاتی', 'ساخت-تیزر-تبلیغاتی', NULL, NULL, 1, 'فیلم و تیزر صنعتی یک ویدیو ۳ دقیقه ای تا ۱۰ دقیقه ایست که از مراحل تولید یک محصول و تجهیزات مورد استفاده در مسیر تولید آن گرفته می شود و اعتبار سازمان یا مجموعه شما را در ذهن مخاطب بیشتر می کند.', 'تیزر تبلیغاتی برای مخاطب شما به منزله ی دیدن تیم متخصص ،تجهیزات و روند کاری شماست.نمایش تیزر صنعتی به مشتریان باعث نشان دادن نکات قوت و مزایای رقابتی شما به آنهاست. داشتن تیزر صنعتی برای صاحبان برند و مجموعه های تولیدی و کارخانه جات نشان صداقت آنها با مشتریان است و برای مخاطبان با هر سطح دانشی اعتماد سازی میکند.', 'images/submenu/2020/d389e5f05702bbe6d37bc7d058553236ساخت تیزر تبلیغاتی.png', 0, 4, NULL, 75, NULL, 1, 1, NULL, '2020-11-05 06:42:30'),
(23, 5, 'عکاسی فیگوراتیو', 'عکاسی-فیگوراتیو', NULL, NULL, 1, 'عکاسی فیگوراتیو برای کسب و کار های مد و لباس بسیار اهمیت دارد به نحوی که یک عکس خوب آن لباس را جذاب و زیبا نشان می دهد و همچنین موجب دقت بیشتر مشتری در انتخاب لباس، در خرید غیرحضوری می شود.', 'و همچنین موجب دقت بیشتر مشتری در انتخاب لباس، در خرید غیرحضوری می شود.', 'images/submenu/2020/c37c26424b10c4dbc2fdc453fde2cb02figour.png', 0, 4, NULL, 29, NULL, 0, 1, NULL, '2020-10-25 15:24:15'),
(24, 5, 'عکاسی تبلیغاتی', 'عکاسی-تبلیغاتی', NULL, NULL, 1, 'پشت پرده هر تبلیغ تاثیر گذار، یک عکاس، با ذهنی خلاق نشسته است. همیشه کسب و کاری موفق تر است که بتواند صادقانه ترین تصویر را از محصول خود برای مخاطبانش به نمایش بگذارد.‌ تا به حال به طور جدی فکر کرده اید، یک عکاس تبلیغاتی با چه هدفی عکس های خود را ثبت میکند؟', 'یک عکس تبلیعاتی به منظور جذب نگاه و ذهن مخاطب به سمت و سوی آن محصول مورد نظر گرفته می شود. در عکاسی تبلیعاتی، ذهن خلاق و دقیق یک عکاس اهمیت بیشتری نسبت به تجهیزات پیشرفته و گران قیمت دارد، در اصل خلاقیت عکاس است که نگاه شما را روی عکس خیره نگه می‌دارد.\r\nجذابیت عکس های تبلیغاتی به پر رنگ و لعاب بودن و یا پر انرژی بودنشان است، عکس های تبلیعاتی باید به گونه گرفته شوند که مخاطب را به هیجان دعوت کرده و او را ترقیب به خرید آن محصول کند.\r\nدر عکاسی تبلیعاتی مهم ترین عناصر، ابتدا نورپردازی، رنگ، ترکیب بندی و فاصله کانونی است. البته که ادیت عکس و آشنایی کامل با نرم افزار های ادیت از اهمیت بالایی برخوردار است.\r\nاین نوع عکس ها معمولا پس زمینه هایی ساده و تک رنگ دارند تا در نگاه اول محصول بیشتر از دیگر عناصر عکس جلب توجه کند.\r\n.\r\nهمانطور که گفتم عکاسی تبلیغاتی به یک ذهن خلاق نیازمند است، اما نباید منکر این موضوع شویم که تجهیزات هم در ایجاد یک عکس تبلیغاتی تاثیر گذار بسیار مهم است.\r\nآژانس تبلیعاتی بستا هم عکاسان حرفه ای و هم تجهیزات به روز را مبنای کار خود در حوزه عکاسی تبلیعاتی قرار داده است، پس شما میتوانید با اطمینان کامل به تیم ما، عکس هایی خیره کننده از محصولات خود در محیط پیرامون و شبکه های اجتماعی مشاهده کنید.', 'images/submenu/2020/c3bdb9047be85983068b9c02061fd50eعکاسی تبلیغاتی.png', 0, 4, NULL, 30, NULL, 0, 1, NULL, '2020-11-05 09:25:55'),
(25, 6, 'چاپ سیلک', 'چاپ-سیلک', NULL, NULL, 1, 'یکی از انواع چاپ که قدمتی طولانی دارد و دارای زیبایی خاصی است اصول کار آن با یک تور ابریشمی و چهارچوبی از جنس فلز یا چوب است.\r\nچاپ سیلک چاپی است که با توجه به قدمت آن هنوز کارایی خود را از دست و امروزه طرفداران خود را دارد.', 'چاپ سیلک از توری ابریشمی که بر روی چهارچوبی فلزی یا چوبی کشیده میشود و طرح چاپ بر روی این تور تشکیل میشود مایعی مثل موم بر روی نقاطی که چاپ نمیشود کشیده میشود سپس اپراتور با یک کاردک رنگ را به روی تور کشیده و طرح چاپی به وجود می آید.\r\nاز معایب آن میتوان به:\r\n1- سرعت پایین \r\n2- محدودیت در کیفیت و رزولیشن\r\n3- محدودیت ترکیب رنگ ها', 'images/submenu/2020/2109f5e9f066a571d50040cb82866183چاپ سیلک اسکرین.png', 0, 4, NULL, 31, NULL, 1, 1, NULL, '2020-11-05 06:44:14'),
(26, 6, 'چاپ افست', 'چاپ-افست', NULL, NULL, 1, 'چاپ افست چاپی است که دامنه محصولات بسیار گسترده ای دارد از مقوا، پارچه، لاستیک تا کارت ویزیت و کاتالوگ و بروشور.در دستگاه چاپ افست عمل اصلی چاپ توسط سه سیلندر(غلطک های استوانه‌ای شکل روی هم قرار گرفته) انجام می شود.', 'در دستگاه چاپ افست سه سیلندر(غلطک استوانه های روی هم قرار گرفته) عمل اصلی چاپ را انجام می دهند جنس اولین استوانه از فلز آلومینیوم است که اصطلاحا به آن زینک میگویند که عبارات چاپ بر روی این استوانه درج میشود سپس به روی استوانه دوم که از جنس لاستیک است منتقل میشود و به کاغذ چاپ منتقل میشود که از بین استوانه دوم و سوم که از جنس کاغذ است عبور میکند.\r\n در چاپ افست از فرمت رنگی cmyk استفاده میشود که هر کدام ستون های مجزا در دستگاه دارند که دارای رنگ های مختلفی هستند که از آن ستون ها به روی کاغذ تزریق میشود.\r\nاز مزایای چاپ افست میتوان موارد زیر را نام برد:\r\nتیراژ بالا\r\nقیمت مناسب \r\nکیفیت عالی \r\nاز معایب آن میتوان به:\r\nزمان بردن عمل چاپ اشاره کرد.', 'images/submenu/2020/6ac16824f84239681876de7da2bdaee4چاپ افست.png', 0, 4, NULL, 32, NULL, 1, 1, NULL, '2020-11-05 06:45:09'),
(27, 6, 'چاپ دیجیتال', 'چاپ-دیجیتال', NULL, NULL, 1, 'یکی از روش های نوین چاپ ، چاپ دیجیتال است که به خوبی جای خود را در میان انواع روش های چاپ باز کرده و در آینده ای نه چندان دور جایگزین تمامی روش های چاپ خواهد شد.\r\n در چاپ دیجیتال میتوانیم تصاویر دیجیتالی را به کاغذ های چاپ انتقال دهیم.', 'در چاپ دیجیتال میتوانیم تصاویر دیجیتالی را به کاغذ های چاپ انتقال دهیم.\r\nچاپ دیجیتال اکثراً در تیراژ های پایین و توسط چاپگرهای لارج فرمت، چاپگرهای لیزری و جوهر افشان حرفه ای انجام میشود.\r\nمزایای چاپ دیجیتال:\r\nامکان سریع آماده شدن\r\n چاپ هر برگه با فرمت دلخواه\r\nصرفه جویی در زمان نسبت به چاپ افست\r\nمعایب چاپ دیجیتال:\r\nبالا بودن هزینه ی آن نسبت به چاپ رقیب افست\r\nو چاپ دیجیتال بر روی تمامی کاغذ ها با جنس های مختلف کارکرد ندارد', 'images/submenu/2020/88c5b244ba5543abb055652cd5539d0cچاپ دیجیتال.png', 0, 4, NULL, 33, NULL, 0, 1, NULL, '2020-11-05 06:46:32'),
(28, 6, 'بسته بندی محصول', 'بسته-بندی-محصول', NULL, NULL, 1, 'در اینجا درباره ی چاپ بسته بندی های زیبا و پر زرق و برقی که باعث نمایش کیفیت بالای محصول و محافظت از سلامت آن می شود و خریدار را ترغیب به خرید آن محصول می کند صحبت میکنیم.', 'به روکش هایی که بر روی کالاها کشیده میشوند تا کالا را از شرایط محیطی مانند گرد و غبار و مواد خورنده و ضربات و فاسد شدن حفظ میکنند و همچنین باعث زیباتر شدن محصول و تشویق مشتریان برای خرید آن میشود بسته بندی میگویند.\r\nنمیتوان تاثیر صنعت چاپ بسته بندی به روی کالا را نادیده گرفت میتوان گفت از تاثیرگذارترین المان برای خرید یک کالا طراحی و چاپ بسته بندی شکیل و خیره کننده است.\r\nدر بسته بندی یک محصول باید به چه نکاتی توجه کرد:\r\n۱-خلاقیت در طراحی بسته بندی\r\n۲-در نظر گرفتن تمامی سلیقه ها از اقشار تا سنین مختلف جامعه\r\n۳-استفاده از رنگ های شاد و متنوع \r\n۴-بهای تمام شده کمتر\r\n۵- بسته بندی باید به دور از خط و خش و شکستگی باشد و شکل آن این احساس را به مشتری القا نکند که این بسته بندی قبلاً باز شده است.\r\n۶- مقاومت و ماندگاری بالا در برابر آسیب و حفظ محصول در برابر فاسد شدن.\r\n۷-خط نوشتاری بر روی بسته بندی باید خوانا و درشت باشد و از رنگ مناسب برای آن استفاده شود.\r\n۸- بسته بندی محصول باید قابل بازیافت باشد.\r\nانواع چاپ در بسته بندی چیست:\r\nچاپ افست، چاپ سیلک، چاپ فلکسو\r\nاز معایب بسته بندی میتوان به:\r\nپر هزینه بودن آن و غیر قابل بازیافت بودن اشاره کرد.', 'images/submenu/2020/1fff53799e3b54ab2e2ddd81753fff3fبسته بندی محصول.png', 0, 4, NULL, 34, NULL, 1, 1, NULL, '2020-11-05 06:47:27'),
(29, 6, 'چاپ رولی', 'چاپ-رولی', NULL, NULL, 1, 'چاپ رول یکی از زیر مجموعه های چاپ افست است . در چاپ رول کاغذ های چاپ یکی پس از دیگری بی وقفه وارد دستگاه چاپ می شوند.و از کاربرد های آن میتوان در چاپ روزنامه ها و تراکت ها اشاره کرد.', 'مزایای چاپ رول:\r\n۱-سرعت بالا\r\n۲-چاپ پشت و رو کاغذ همزمان\r\n۳-عملیات تا زدن کاغذ بلافاصله پس از چاپ\r\n۴- جلوگیری از اتلاف زمان', 'images/submenu/2020/664da7da6ab40b06836bd4d0b98d32ddچاپ رولی.png', 0, 4, NULL, 35, NULL, 0, 1, NULL, '2020-11-05 06:48:14'),
(30, 6, 'چاپ بنر و فلکسی', 'چاپ-بنر-و-فلکسی', NULL, NULL, 1, 'به بنر هایی که دارای نور هستند به عبارت دیگر بنر هایی که یک چشمه نور پشت آن قرار می گیرد(تابلوها) و نور را از خود عبور می دهند فلکسی می گویند  و تفاوت آن با بنر در توانایی عبور نور از خود است.', 'از مزایای چاپ فلکسی صرفه جویی در هزینه ها و زیبا تر و درخشان تر  نسبت به بنر و قابلیت تعویض آسان برای تنوع بخشیدن به تبلیغ میتوان نام برد جنس چاپ فلکسی از نوع pvc است و به دلیل ماندگاری و مقاومت بالا مخاطبان بیشتری را به خود جذب میکند.', 'images/submenu/2020/d109f9d83b2302e7efd6c3062b2315a0چاپ بنر.png', 0, 4, NULL, 36, NULL, 0, 1, NULL, '2020-11-05 06:48:53'),
(31, 4, 'طراحی برند بوک', 'طراحی-برند-بوک', NULL, NULL, 1, 'برند بوک یک هویت سازمانی کامل از هر آنچه که یک برند برای برندینگ خود نیاز دارد.', 'به دفترچه برند که اطلاعات برند از قبیل رنگ آن ، نوع فونت ، هویت برند و تاریخ به وجود امدن آن، داستان به وجود آمدن آن، شخصی که آن را طراحی کرده و ... در آن وجود دارد برند بوک می گویند.\r\nچرا باید برند بوک داشته باشیم:\r\nوجود برند بوکی که به زبان ساده اطلاعات برند را به مشتریان یا همکاران تازه وارد به شرکت یا سازمان یا ... منتقل میکند ضروری است زیرا به معرفی هر چه بهتر برند با جزییات کمک بسیار شایانی می کند.', 'images/submenu/2020/1cff2b5af4374d4ebf08a4b779ea0460طراحی برند بوک.png', 0, 4, NULL, 37, NULL, 0, 1, NULL, '2020-11-05 06:49:30'),
(32, 4, 'طراحی اوراق اداری', 'طراحی-اوراق-اداری', NULL, NULL, 1, 'از ملزومات متنوع و شکیل که هر شرکتی باید داشته باشد و معرف لوگوی شرکت و تاثیرگذار در جذب مشتریان می باشد.', 'اوراق اداری به اوراقی میگویند که برای ایجاد ارتباطاتی مثل نامه نگاری ایجاد مکاتبات اداری با سایر شرکت ها و سازمان های دیگر گفته میشود.\r\nاوراق اداری تشکیل شده از:\r\nپاکت نامه a4/a5\r\nکارت ویزیت\r\nسر برگ a4/a5\r\nدر طراحی اوراق اداری باید به نکات زیر توجه کرد:\r\nضرورت وجود لوگو و اسم شرکت.\r\n تا حد امکان پرهیز از شلوغی زیاد اوراق از طرح ها.\r\nاستفاده از رنگ بندی های متناسب با لوگو و فونت مناسب کسب و کار.', 'images/submenu/2020/b0ccd349849d8a8dec473535a7908489طراحی اوراق اداری.png', 0, 4, NULL, 38, NULL, 0, 1, NULL, '2020-11-05 06:50:04'),
(33, 4, 'طراحی مجله', 'طراحی-مجله', NULL, NULL, 1, 'به کتابچه های رنگارنگ همراه با تصاویر زیبا و جذاب که همیشه جز نشریه های پرطرفدار هستند و با یک دیگر رقابت تنگاتنگی در جذب خواننده بیشتر دارند.\r\nنمیتوان نقش مجلات در انتشار اخبار، اتفاقات و... را انکار کرد در حین این که وسیله ی خوبی برای سرگرمی نیز هستند.', 'مجله به نشریه ای می‌گوییم که طبق یک زمان مشخص منتشر میشود و تعداد این انتشار در سال بیش از دو بار است.\r\nمجله میتواند هفتگی, ماهانه, سه ماهه انتشار یابد.\r\nموضوعات مندرج در مجله تشکیل شده از مطالب سیاسی، علمی، ادبی و مطالب سرگرمی و ... است.\r\nمهم ترین گزینه در طراحی مجله طراحی جلد آن است که بیشترین مخاطب را بسوی خود می‌کشاند البته از طراحی داخل مجله هم نباید غافل شد زیرا مخاطب پس از دیدن جلد به سراغ داخل مجله میرود درون مجله تصاویر باید از کیفیت و گرافیک بسیار خوبی برخوردار باشند تا مخاطب را به خود جذب کنند همچنین باید از رنگ های شاد و متنوع خلاقیت در طرح ها و فونت خوانا و خوش رنگ استفاده کرد و همچنین اگر مجله در رابطه با کسب و کار خاصی است حتما از مطالب و تصاویر مرتبط با کسب و کار استفاده شود.\r\nیک مجله خوب از تمامی لحاظ میتواند تبلیغ بسیار خوبی از کسب و کار شما کند.\r\nدر این برهه از زمان شرکت ها و کسب و کار های پیشرفته در زمینه ی کاری خود باید مجله ای در خور شخصیت کاری خود داشته باشند تا هر بیشتر و بهتر معرف آنان در نظر افراد جامعه باشد.', 'images/submenu/2020/3e87f2c1e1cdf812251498034aec6ed3طراحی مجله.png', 0, 4, NULL, 39, NULL, 0, 1, NULL, '2020-11-05 08:32:46'),
(35, 4, 'طراحی تقویم و سررسید', 'طراحی-تقویم-و-سررسید', NULL, NULL, 1, 'تقویم و سررسید می‌توانند مانند یک پکیج،برای تبلیغ کسب و کارتان به افراد و یا مجموعه های مختلف هدیه داده شود و برند شما را در دل مخاطبان محبوب تر سازد.', 'شما میتوانید با طراحی زیبای تقویم و سررسید و پخش آنها بین مخاطبان خود کاری کنید که افراد هر روز با دیدن هدایای شما به یاد برند و جایگاه کسب و کارتان بیافتند.\r\n.\r\n.\r\nسررسید ابزاری ست بسیار پر کاربرد که معمولا به شکل کتاب یا دفتر است. محتوای درون یک سررسید می‌تواند از تبریک سال نو تا تقویم و علامت گذاری تمام مناسبت های سال و قسمت هایی برای یادداشت های روزانه باشد.\r\nهمچنین مجموعه ها می‌توانند یادداشت هایی به دلخواه خود و با هر موضوعی اعم از تبلیغاتی،اقتصادی،فرهنگی و... سفارش دهند تا درون سررسید هایشان چاپ شود.\r\nتقویم ها نیز برای هدیه دادن و تبلیعات، اکثرا به صورت رو میزی و کوچک با طرح هایی شکیل و چشم نواز طراحی می‌شوند.\r\nاولین و مهم ترین گام برای داشتن تقویم و سررسید های زیبا و مناسب با هویت و شخصیت شما، طراحی خلاقانه،با دقت و هوشمندانه آنهاست.\r\nبرای آنکه تقویم و سررسید های شما پس از دریافت مخاطب به کناری انداخته نشود و خاک نخورد،باید طراحی مناسب و جذابی داشته باشد.', 'images/submenu/2020/991ab6fe1563c62f24a072849fbf1bbbطراحی تقویم و سررسید.png', 0, 4, NULL, 40, NULL, 0, 1, NULL, '2020-11-05 08:46:31'),
(36, 4, 'طراحی هدیه تبلیغاتی', 'طراحی-هدیه-تبلیغاتی', NULL, NULL, 1, 'طراحی هدیه تبلیغاتی، طراحی بر روی انواع هدایای تبلیغاتی از جمله فلش و پاور بانک است‌. با هدیه دادن به مشتریان خود به آنها این باور را می‌رسانیم که برایشان ارزش بسیاری قائل هستیم.از تاثیر گذارترین فعالیت ها در حوزه تبلیغات هدایای تبلیغاتی است.', 'باید در طراحی تبلیغاتی به نکات زیر توجه کرد:\r\nاز رنگ های متنوع و شاد و متناسب با لوگو استفاده شود.\r\nفونت خوانا و رنگ مناسب با آن انتخاب شود.\r\nحتما در طراحی هدایای تبلیغاتی لوگو و اسم شرکت گنجانده شود.\r\nبه سلایق مشتری ها و مخاطب توجه شود.\r\nاز جنس های مرغوب برای هدایا استفاده کنید.', 'images/submenu/2020/b21b0c8a73216a715952e0848e3f03b1طراحی هدیه تبلیغاتی.png', 0, 4, NULL, 41, NULL, 0, 1, NULL, '2020-11-05 08:49:06'),
(37, 4, 'طراحی پوستر', 'طراحی-پوستر', NULL, NULL, 1, 'پوستر ها کاغذ هایی اغلب در ابعاد A4هستند که اخباری را در قالب متن،تصویر،طرح و... با عموم به اشتراک می‌گذارند. طراحی پوستر تبلیغاتی در کمترین زمان و با کمترین هزینه انجام می شود.', 'پوسترها معمولا در اماکن شلوغ و پر رفت و آمد،بر روی دیوار ها،پشت شیشه مغازه ها و... چسبانده یا پخش می‌شوند.\r\nطراحی پوستر اهداف مختلفی دارد، پوستر با موضوع تبلیغات،ترویج یک رفتار فرهنگی،استخدام افراد و...\r\nکه در بین انواع اهداف پوستر، پوسترهای تبلیغاتی رایج تر هستند.\r\nپوسترها به دلیل پخش وسیع و سریع آنها د بین مردم،میتواند در بهبود بازاریابی بسیار کمک کند.\r\nاما نکته بسیار مهمی که در خصوص پوسترهای تبلیغاتی وجود دارد این است که، به دلیل تکثیر بسیار وسیع آنها در دوره های اخیر،کمتر کسانی هستند که به آنها توجه کنند و جذبشان شوند،\r\nپس ما چگونه میتوانیم از پوستر برای بهبود فروش و جذب مخاطب استفاده کنیم؟\r\nپاسخ این سوال را می‌توان در قسمت طراحی پوستر پیدا کرد.\r\nشما با طراحی ساده و در عین حال زیبای المان های یک پوستر(انتخاب مناسب و متنوع از رنگ ها،استفاده از فونت های زیبا و خوانا و تصاویر مرتبط و جذاب)می‌توانید مردم را در هر سن و با هر سلیقه ای،جذب محتوای پوستر کرده و سپس آنها را تبدیل به مشتری کنید.', 'images/submenu/2020/aa029939134772073ad65ed7d82eadeaطراحی پوستر.png', 0, 4, NULL, 42, NULL, 0, 1, NULL, '2020-11-05 08:49:47'),
(39, 4, 'ساخت موشن گرافیک', 'ساخت-موشن-گرافیک', NULL, NULL, 1, 'در واقع ساخت موشن گرافیک از مهیج ترین،دشوار ترین و پر درآمد ترین حوزه های فیلم سازی،انیمیشن و... به شمار می رود. در تعریف ساده، جان بخشیدن به عکس ها را موشن گرافیک می گویند.', 'موشن گرافیک به توانایی جان بخشیدن و به حرکت درآوردن تصاویر و خلق شخصیت های جدید و حرکت دادن آن ها به منظور تفهیم هدفی خاص را موشن گرافیک میگویند.\r\n نرم افزارهایی که میتوان با کمک آن ها موشن گرافیک تولید کرد:\r\nAdobe after effects-1\r\nAdobe Photoshop-2\r\nAdobe illustrator-3\r\nAdobe InDesign-4\r\nAdobe premiere-5\r\nدرست است که نرم افزار فتوشاپ به خودی خود قادر به ساختن موشن گرافیک نیست ولی در ویرایش عکس ها نقش بسزایی ایفا میکند.\r\n کاربرد های موشن گرافیک:\r\nساخت انیمیشن های:\r\nدوبعدی \r\nسه بعدی\r\nساخت تیزر های تبلیغاتی\r\nساخت بخش های گرافیکی فیلمها و فضای مجازی\r\nانواع موشن گرافیک:\r\nاینفوموشن\r\nاستاپ موشن\r\nویدئو موشن\r\nموشن گرافیک', 'images/submenu/2020/b0f3403111f6cc5e51a9e945ec802f7dطراحی موشن گرافیک.png', 0, 4, NULL, 44, NULL, 0, 1, NULL, '2020-11-05 08:50:44'),
(40, 3, 'خرید ریپورتاژ حرفه ای', 'خرید-ریپورتاژ-حرفه-ای', NULL, NULL, 1, 'ریپورتاژ آگهی فقط یک آگهی ساده نیست، بلکه یک خلاصه و چکیده با اهمیت درباره کسب و کار شماست. ریپورتاژ مسئله بسیار مهمی است که وجود آن باعث افزایش اعتبار برند شما می شود.', 'در اصل رپورتاژ آگهی یک گزارش بدون جانبداری از محصول خاصی است و به ارائه اطلاعات درباره آن محصول میپردازد.\r\nاما در واقع این گزارش آنچنان هم بی طرفانه نیست بلکه تلاش برای نشان دادن بی طرفی است ولی در حقیقت استراتژی جذب مشتری از طریق آشنا کردن آنان با برند به صورت یک مقاله است.\r\nیکی از مزایای رپورتاژ آگهی افزایش سئو سایت است و همانطور که میدانید با افزایش سئو سایت، سایت شما در صفحه اول گوگل مینشیند و همین امر باعث پربازدید شدن سایت میشود.', 'images/submenu/2020/50b688d5bd59ad29ba843684ecd36fc2خرید ریپورتاژ حرفه ای.png', 0, 4, NULL, 45, NULL, 0, 1, NULL, '2020-12-01 07:05:26'),
(44, 5, 'عکاسی تبلیغاتی غذا', 'عکاسی-تبلیغاتی-غذا', NULL, NULL, 1, 'چگونه خوشمزگی ها را از نگاه مشتری هایمان ببینیم؟!\r\nبا تاکید بر مقدار لذیذ بودن غذا ها توسط عکاسی تبلیغاتی غذا، هر روز مشتری های بیشتری را نشسته بر روی صندلی های درون رستوران خود مشاهده کنید.', 'عکاسی تبلیغاتی غذا بهترین روش برای تبلیغات مواد غذایی است. وقتی از مواد غذا هایتان  عکس های اصولی با خلاقیت و کیفیت بالا، به اشتراک می گذارید، مطمئن باشید که مخاطبان خود را گرسنه کرده اید، و همین عمر باعث می‌شود مخاطب نام تجاری شما در ذهنش بماند و حداقل با خود بگوید:\"حتما این غذا رو سفارش میدم،به نظرم خیلی خوشمزه ست.\"\r\nالبته که عکاسی تبلیغاتی غذا تنها برای غذا ها و رستوران ها نیست،بلکه از عکاسی تبلیغاتی غذا برای تبلیعات در صنایع غذایی بزرگ مثل کارخانه جات مواد غذایی، کسب و کار های اینترنتی مربوط به این حوزه، طراحی منو هتل های بزرگ و کوچک و... استفاده می‌شود.\r\nدر عکاسی تبلیغاتی غذا یکی از عوامل مهم بعد از ذهن خلاق و دوربین و لنز مناسب، مخلفات مربوط به هر غذا یا مواد خوراکی است، آنها به عکس و غذا جلوه خاصی می‌بخشند.', 'images/submenu/2020/473a41897a3d6a1c05deeda93858e635food.png', 0, 4, NULL, 49, NULL, 1, 1, NULL, '2020-10-25 15:25:37'),
(45, 5, 'عکاسی ۳۶۰ درجه از محصول', 'عکاسی-۳۶۰-درجه-از-محصول', NULL, NULL, 1, 'عکاسی ۳۶۰ درجه را میتوان عکاسی ۳ بُعدی هم دانست، با این نوع از عکاسی به مشتری امکان این را می دهیم که از هر طرف عکس محصول مورد نظر خود را ببیند و دقیق تر آن را انتخاب و خریداری کند.', 'اگر می خواهید مشتری با اطمینان و رضایت کامل از محصولات شما خریداری کند، حتما از محصولات خود عکس ۳۶۰ درجه داشته باشید، زیرا با عکاسی ۳۶۰ درجه انتخاب مشتری دقیق تر است.\r\nبرای رسیدن به یک عکس ۳۶۰ درجه جذاب، ابتدا از محصول خود چند عکس با زوایای مختلف و معمولا کادر عمودی گرفته و سپس آن هارا در نرم افزار های ادیت، به یک عکس ۳۶۰ درجه تبدیل میکنیم.\r\nعکاسی ۳۶۰ درجه از محصولات بسیار در جذب مخاطبان کسب و کار های اینترنتی‌ کمک میکند، چون این روش در عکاسی در بین مردم محبوبیت بسیار زیادی پیدا کرده است.\r\nما برای عکاسی ۳۶۰ درجه از محصولات شما، از دوربین های حرفه ای با لنز های مناسب، نورپردازی و چیدمان مخصوص این نوع عکس  استفاده میکنیم تا یک عکس ۳۶۰ درجه حرفه ای و جذاب خروجی تلاشمان باشد.', 'images/submenu/2020/87b10c3935a0f3dc2c888a615be27c2atoor.png', 0, 4, NULL, 50, NULL, 0, 1, NULL, '2020-10-25 15:28:23'),
(46, 5, 'عکاسی لوازم آرایشی', 'عکاسی-لوازم-آرایشی', NULL, NULL, 1, 'عکاسی از لوازم آرایشی که یکی از شاخه های مهم عکاسی تبلیعاتی است، باید به گونه ای باشد که هم احساس لطافت و زیبایی، و هم حس اطمینان کامل را نسبت به محصول به مخاطب القا کند.', 'در بحث فروش لوازم آرایشی از طریق شبکه های اجتماعی، مهم ترین قسمت، عکاسی از لوازم آرایشی است.\r\nحتی اگر کسب و کارتان به شکل سنتی است، ابتدا برای ارتباط با مخاطبان و مشتریان بیشتر در شبکه های اجتماعی یا مجازی فعالیت خود را شروع کرده و سپس از محصولات آرایشی و بهداشتی خود عکس های با کیفیت و حرفه ای ثبت کنید و به اشتراک بگذارید تا ببینید چقدر در جذب مشتری موفق تر از قبل خواهید بود.\r\nنکته هایی که برای عکاسی خوب از لوازم آرایشی و بهداشتی مهم هستند:\r\n○شناختن کامل ویژگی های محصول\r\n○شناختن مخاطبان خود\r\n○به کار گیری رنگ های مناسب و گاهی متضاد در کادر\r\n○استفاده از سطوح مختلف(برجسته و...) برای پس زمینه عکس\r\n○قرار گیری با دقت جزئیات در کادر عکس ها', 'images/submenu/2020/3ef2c74e2a519be13a04946e4b1d98c8عکاسی لوازم آرایشی.png', 0, 4, NULL, 51, NULL, 0, 1, NULL, '2020-11-05 09:27:12'),
(47, 5, 'عکاسی تور مجازی', 'عکاسی-تور-مجازی', NULL, NULL, 1, 'در عکاسی تور مجازی شما دیگر حسرت حضور نداشتن در اماکن را نمی‌خورید. اما چگونه میتوانیم تنها با دیدن، حس حضور واقعی را در مکان مورد نظر داشته باشیم؟ عکاسی تور مجازی مجموعه ای فراگیر از عکس های ۳۶۰ درجه در کنار هم است که به شدت مخاطب را تحت تاثیر قرار می‌دهد.', 'عکاسی تور مجازی مجموعه ای فراگیر از عکس های ۳۶۰ درجه در کنار هم است که به شدت مخاطب را تحت تاثیر قرار می‌دهد.\r\nاگر شما یک هتل لوکس راه اندازی کرده اید و می‌خواهید آن را از تمام زوایا در یک قاب برای مخاطبان خود از سرتاسر جهان به نمایش بگذارید، بهترین و موثر ترین روش، عکاسی تور مجازی است. در واقع عکاسی تور مجازی این حس را به مخاطب می‌دهد که هم در خانه امن و راحتش نشسته است و هم در حال قدم زدن در راه روی هتل شماست. \r\nشما با به اشتراک گذاشتن عکاسی تور مجازی در رابطه با مکان کسب کارتان برای مخاطبان، دیگر نیاز به توضیح درباره ظاهر مکان خود ندارید.\r\nعکاسی تور مجازی برای کسب و کارهایی مانند: رستوران ها، مراکز تفریحی و گردشگری، هتل ها، گالری ها یا نمایشگاه ها، بیمارستان ها، کارخانجات، مجتمع های تجاری و... مناسب است.', 'images/submenu/2020/38d5dadd32405bbbe7d14825f76c2c73digital-nomad-animate.png', 0, 4, NULL, 52, NULL, 1, 1, NULL, '2020-10-25 15:28:46'),
(48, 5, 'عکاسی معماری', 'عکاسی-معماری', NULL, NULL, 1, 'عکاسی معماری کمی پیچیده تر و تخصصی تر است، باید در یک عکس زوایای یک معماری را نمایش دهید، و برای موفق بودن به مهارت و آگاهی از اصول عکاسی معماری نیازمند هستیم. ما در ادامه، مطالبی در این خصوص برای شما آماده کرده ایم.', 'اشاره ب برخی از نکات مهم در عکاسی معماری:\r\n◇برخلاف عکس از محصولات و غذا، در عکاسی معماری باید عمق میدان بیشتری داشته باشیم تا تمام جزئیات پس زمینه در عکس ما نمایان باشد.\r\n◇معمولا در عکاسی معماری ترجیح می‌دهیم از نور طبیعی در عکس هایمان استفاده کنیم، بنابراین باید زمان مناسبی در روز را برای عکاسی در‌نظر بگیریم. همچنین نورپردازی طبیعی به ما کمک میکند تا رنگ ها را با صداقت بیشتر در کادر عکس به نمایش بگذاریم.  \r\n◇ترکیب بندی در عکاسی معماری بسیار اهمیت دارد، اگر یک ترکیب بندی مناسب و ساده در عکس داشته باشیم می‌توانیم توجه مخاطب را به خطوط عکس و الگو ها که یکی از اصول عکاسی معماری است جلب کنیم، پس سعی میکنیم تا حد امکان موارد حاشیه ای که حواس مخاطب را به خود پرت میکند را از کادر خارج کنیم.\r\nبرای نشان دادن نمای بیرونی یک ساختمان زیبا و یا چیدمان داخل ساختمان باید تمام این نکات رعایت شوند، البته که وجود دوربین و به خصوص لنز پر قدرت به ما در این مسیر بسیار کمک میکنند اما اگر دانش و مهارت کافی نداشته باشیم، عکس هایمان نمی‌تواند چشم و ذهن مخاطب را درگیر کند.', 'images/submenu/2020/39564a809e6137277824c081016b07bahouse-restyling-animate.png', 0, 4, NULL, 53, NULL, 0, 1, NULL, '2020-10-25 15:27:49'),
(49, 5, 'عکاسی پوشاک', 'عکاسی-پوشاک', NULL, NULL, 1, 'عکاسی تبلیغاتی پوشاک بهترین و تاثیر گذار ترین روش برای فروش هر چه بیشتر پارچه ها و پوشاک است، اگر در کسب و کارتان با پارچه و پوشاک سر و کار دارید، حتما عکاسی تبلیعاتی پوشاک را در اولویت کار خود قرار دهید، به خصوص اگر تمام یا قسمتی از کارتان به صورت اینترنتی باشد.', 'مشتری ما چگونه میتواند از پشت سیستم و اینترنت پارچه ها را لمس کند و بهترین انتخاب را داشته باشد؟!\r\n\r\nعکاسی تبلیغاتی پوشاک بهترین و تاثیر گذار ترین روش برای فروش هر چه بیشتر پارچه ها و پوشاک است. \r\nممکن است بعضا در شبکه های اجتماعی لباسی را دیده باشید که با تمام وجود می‌خواهید آن لباس در کمد شما باشد، این حجم از علاقه نسبت به یک پارچه را تنها یک عکاس تبلیعاتی پوشاک میتواند در ذهن شما ایجاد کند.\r\nدر عکاسی تبلیغاتی، عکاسان تلاش میکنند تا با نورپردازی کاملا دقیق و حرفه ای، بافت پارچه را به صادقانه ترین شکل ممکن در اختیار چشمان مخاطبان به نمایش بگذارند. این عمر نیز در رنگ های پارچه ها بسیار اهمیت دارد، که از دید مشتری تمام مشخصات لباس چه از نزدیک یا چه در عکس کاملا شبیه به هم باشند.\r\nبله، این صداقت است که همیشه مخاطب را راضی نگه می‌دارد.', 'images/submenu/2020/284813bf8076596ff4feac0f1a64fa0bchoosing-clothes-animate.png', 0, 4, NULL, 54, NULL, 1, 1, NULL, '2020-10-25 15:27:22'),
(50, 5, 'عکاسی لوازم صنعتی', 'عکاسی-لوازم-صنعتی', NULL, NULL, 1, 'عکاسی صنعتی،عکاسی از قطعات و محصولات صنعتی است که معمولا به تعداد زیاد و در اماکنی مانند کارخانه یا کارگاه ثبت میگردد. در این حوزه از عکاسی تبلیغاتی، عکاسان معمولا از دید فنی به محصول می نگرند و عکس های خود را ثبت می کنند.', 'در واقع در عکاسی صنعتی تلاش میکنیم تا تمام جزئیات یه محصول و یا قطعه را به درستی ثبت کنیم. در عکاسی صنعتی به ترکیب بندی و چیدمان خیلی توجه نمی‌شود و معمولا از یک استاندارد خاص و یکسان تبعیت می‌شود. \r\nبرای مثال عکاسی از مراحل تولید یک تلفن و یا محصولات فروشگاه اینترنتی بزرگ، عکاسی صنعتی محسوب می‌شود.\r\nمعمولا در عکاسی صنعتی پس زمینه های ساده و تک رنگ(سفید و مشکی و..)کاربرد بیشتری دارند.', 'images/submenu/2020/e80b663fc7892a40fc50e6d0102a9b39عکاسی لوازم صنعتی.png', 0, 4, NULL, 55, NULL, 0, 1, NULL, '2020-11-05 09:28:16'),
(51, 5, 'عکاسی طلا و جواهرات', 'عکاسی-طلا-و-جواهرات', NULL, NULL, 1, 'یکی دیگر از حوزه های دشوار در عکاسی تبلیغاتی، عکاسی از طلا و جواهرات می‌باشد. نشان دادن بافت، درخشش و زیبایی خیره کننده جواهرات فقط می‌تواند کار یک عکاس حرفه ای در حوزه عکاسی طلا و جواهرات باشد.', 'در عکاسی از جواهرات بهتر است ابتدا از تمیز بودن قطعه مورد نظرتان مطمئن شوید، سپس اگر می توانید یک لنز ماکرو تهیه کنید، با این لنز می‌توانید جواهرات را در‌عکس هایتان زیبا تر و بزرگ‌تر از اندازه واقعی خود جلوه دهید.\r\nگام بعد حفظ سادگی کادر عکس است، برای نشان دادن ظرافت، شکوه و زیبایی جواهرات هرچه پس زمینه و کادر ساده تری داشته باشید موفق تر هستید‌.\r\nحتما از بهترین نورپردازی استفاده کنید. فلزات و سنگ هایی که در طلا و جواهرات به کار رفته است این کار را دشوار می‌کند، اما شما با تغییر دادن جهت سوژه یا نور سعی کنید سایه ها و انعکاس نور بر روی جواهر را نرم تر کنید.\r\nبرای ثبت یک عکس شفاف و دقیق، بهتر است از سه پایه و ریموت استفاده کنید، در این حالت دوربین شما هنگام شاتر زدن ذره ای تکان نمی‌خورد و عکس به هیچ عنوان تار یا کدر نمی‌شود.\r\nاین نکته را در نظر بگیرید که حتما فوکوس دقیقی روی طلا یا جواهر داشته باشید. بهتر است برای دقیق تر شدن کارتان از فوکوس دستی دوربین استفاده کنید، زمانی که شما دوربین خود را روی فوکوس خودکار تنظیم میکنید ممکن است دوربین به درستی و دقیق فوکوس را انجام ندهد.\r\nدر انتخاب رنگ و چیدمان کادر دقت کنید تا عکسی مطلوب و چشم گیر ثبت کنید.', 'images/submenu/2020/7a4bcc8ad1448ad3ef83e7eb3f19f7b7عکاسی طلا وجواهرات.png', 0, 4, NULL, 56, NULL, 0, 1, NULL, '2020-12-01 07:06:04'),
(52, 5, 'ماکروگرافی', 'ماکروگرافی', NULL, NULL, 1, 'گرفتن عکس با کیفیت در فاصله بسیار نزدیک از هر چیز کوچک را ماکروگرافی میگوییم. برای عکاسی ماکرو یا ماکروگرافی مستلزم رعایت و به وجود اوردن یک سری شرایط اعم از خرید لنز مناسب، نور مناسب و... هستیم.', 'با عکاسی ماکرو می توانید محصول خود را بزرگ تر، زیبا تر و مهم تر از مواقع دیگر به مخاطبان خود نشان دهید.\r\nدر ماکروگرافی ابتدا باید یک لنز ماکرو تهیه کنید و سپس سعی کنید این اصول را رعایت کنید:\r\n○در عکاسی ماکرو فوکوس را روی مود دستی تنظیم کنید، نه خودکار!\r\n○از رینگ معکوس که بین بدنه لنز و دوربین قرار میگیرد استفاده کنید.\r\n○از رینگ فلاش استفاده کنید.\r\n○حتما یک سه پایه و ریموت برای عکاسی ماکرو داشته باشید.\r\n○از نور مناسب که ترجیحاً نور آفتاب صبحگاهی است برای نشان دادن بهتر جزئیات استفاده کنید.', 'images/submenu/2020/607e939d6ce8f1b3a3ca22fa398074d4ماکروگرافی.png', 0, 4, NULL, 57, NULL, 0, 1, NULL, '2020-12-01 07:06:18'),
(53, 5, 'عکاسی مدلینگ', 'عکاسی-مدلینگ', NULL, NULL, 1, 'عکاسی مدلینگ یکی از شاخه های بسیار تاثیر گذار و مهم عکاسی تبلیغاتی است. طراحان و صاحبان برند مد و پوشاک برای اثر بخشی بیشتر و ترغیب مشتری به خرید محصولات از مدل های مشهور استفاده میکنند تا کاربرد محصولاتشان را به درستی به نمایش بگذارند.', 'امروزه عکاسی مدلینگ بسیار در شبکه های اجتماعی پر کاربرد و پر بازده است.\r\nعلاوه بر این عکاسی مدلینگ دارای فوت و فن های دقیقی است که رعایت نکردن آنها سبب اثر منفی در ذهن مشتری میشود. ما در آژانس تخصصی تبلیغات بستا با متخصص ترین افراد این رشته در خدمت شما هستیم تا منحصر به فرد ترین نتیجه را به شما ارائه دهیم.', 'images/submenu/2020/47751aaaec3663cff913a65b1e80bf17figour.png', 0, 4, NULL, 58, NULL, 0, 1, NULL, '2020-10-25 15:26:56'),
(55, 5, 'ساخت مستند', 'ساخت-مستند', NULL, NULL, 1, 'ما برای ساختن یک مستند تبلیغاتی، تلاش می‌کنیم تا هم واقعیت و هم زیبایی کسب و کار شما را در قالب فیلم(مستند) به مخاطبانتان نشان دهیم. اگر می‌خواهید مخاطبانتان با روند کاری و یا محیط کاری کسب و کارتان به طور کامل آشنا شوند،از مستند تبلیغاتی استفاده کنید.', 'یک فیلمی که به صورت مستند ساخته می شود حاوی اطلاعاتی کامل است که معمولا با سخن و به تصویر کشیدن آیتم های مورد نظر به مخاطب منتقل می شود.\r\nاگر می‌خواهید مخاطبانتان با روند کاری و یا محیط کاری کسب و کارتان به طور کامل آشنا شوند، از مستند تبلیغاتی استفاده کنید.\r\nاولین گام شما در این مسیر انتخاب هدف برای ساخت مستند تبلیغاتی است، ابتدا باید مشخص کنید که هدفتان از این کار افزایش فروش محصولات و خدمات، یا معرفی نام تجاری و محیط کسب و کارتان است. همچنین باید سن و مشخصات هویتی تقریبی مخاطبان خود را شناسایی کنید تا بر این اساس قالب مستند را طراحی کنید.\r\nصداقت هر چه بیشتر شما در تهیه مستند، ترکیب بندی و ترکیب رنگ مناسب برای پس زمینه،استفاده از دوربین های حرفه ای و با کیفیت، صدا گذاری مناسب و حرفه ای روی ویدئو و... \r\nمیتوانند در ساخت یک مستند حرفه ای و موثر به شما کمک کنند.', 'images/submenu/2020/fc651c37b3c4f2d6a9b44e1ffb01c84eساخت مستند.png', 0, 4, NULL, 60, NULL, 0, 1, NULL, '2020-11-05 06:53:22');
INSERT INTO `submenus` (`id`, `menu_id`, `title`, `slug`, `class`, `icon`, `route_active`, `description`, `text`, `images`, `count`, `status`, `keycheck`, `service_id`, `tags`, `footer_show`, `user_id`, `created_at`, `updated_at`) VALUES
(56, 6, 'چاپ لمینت', 'چاپ-لمینت', NULL, NULL, 1, 'به روکش های مات و براق زیبایی که بر روی محصولات چاپی کشیده می شوند لمینت گوییم، وجود یک سپر یا محافظ برای محصولات چاپی بسیار ضروری است تا از آسیب ها در امان باشند، در واقع لمینت یک نوع از چاپ نیست بلکه روکشی است که بعد از اتمام عمل چاپ بر روی محصولات کشیده میشود.', 'به طور کلی میتوانیم لمینت ها را به دو دسته چاپ لمینت سرد و چاپ لمینت گرم و همچنین چاپ لمینت ویژه که ترکیبی از دو روش سرد و گرم( از این نوع لمینت برای استند های نمایشگاهی استفاده میشود) است تقسیم کنیم.\r\nجنس چاپ لمینت از نوع pvc است.\r\nهر کدام از دو روش چاپ لمینت سرد و گرم به دو مدل مات و براق تقسیم میشوند.\r\nتفاوت بین چاپ لمینت سرد و گرم:\r\nدر لمینت گرم باید حتماً چسب گرم شود تا بر روی محصولات چسبیده شود.\r\nاما در لمینت سرد احتیاجی به گرم کردن چسب نیست و در حالت سرد به محصول چسبیده میشود.\r\nاز لحاظ زیبایی چاپ لمینت سرد از گرم بهتر است.\r\nبه چه دلیل باید از چاپ لمینت استفاده کنیم:\r\nبه دلیل محافظت از محصول در برابر تا خوردگی\r\nشکسته شدن \r\nرنگ پریدگی\r\nلک شدن \r\nحفاظت از سرما و گرما', 'images/submenu/2020/4c0c32a330037c0c37a392b33b2b7d49چاپ لمینت.png', 0, 4, NULL, 61, NULL, 0, 1, NULL, '2020-11-05 06:54:45'),
(57, 6, 'چاپ فلت بد', 'چاپ-فلت-بد', NULL, NULL, 1, 'چاپ فلت بد یکی از انواع چاپ های پرطرفدار و زیبا که به چاپ بر روی اجسام سخت معروف است.', 'جز انواع چاپ های نوین و یکی از زیر مجموعه های چاپ دیجیتال است و گستردگی زیادی دارد و به چاپ بر روی اجسام سخت معروف است مانند: سنگ- چوب- شیشه و...', 'images/submenu/2020/cc381f756e8b4d14c655f657cc204510چاپ فلت بد.png', 0, 4, NULL, 62, NULL, 0, 1, NULL, '2020-11-05 06:55:11'),
(58, 6, 'چاپ هدایا تبلیغاتی', 'چاپ-هدایا-تبلیغاتی', NULL, NULL, 1, 'از بهترین کارها و موثرترین در جذب مشتری هدایای تبلیغاتی می‌باشد هدیه دادن همیشه حس با ارزش بودن را منتقل میکند و شما میتوانید با این کار مشتری های دایمی زیادی را جذب خود کنید.برای آشنا شدن با یکی از بازاریابی های جذاب و غافلگیر کننده با ما همراه باشید.', 'بیشتر شرکت ها برای ارائه محصولات جدید و آشنا شدن مشتریان با آن ها همچنین جذب مشتری و نگهداری مشتری های سابق از هدایای تبلیغاتی در ابعاد و اشکال و رنگ بندی های زیبا و متنوع استفاده میکنند.\r\nانواع هدایای تبلیغاتی:\r\nست اداری مانند: رومیزی، خودکار، دفترچه، سررسید، فلش مموری ، پاور بانک،  مونوپاد، و...\r\nبهترین هدیه تبلیغاتی چه چیزی می باشد؟\r\nبهترین هدیه تبلیغاتی هدیه ای است که مربوط به حوزه ی کاری باشد و باعث مرور خاطرات همکاری خوب شما با مخاطب باشد.\r\nحتما باید برند شرکت بر روی هدایا تبلیغاتی درج شود.\r\nدر انتخاب جنس هدایا دقت ویژه ای کنید چون اگر هدایا از جنس مرغوبی تشکیل نشده باشند ممکن است هدف شما برای جذب مشتری نتیجه معکوس دهد.\r\nدر طراحی و ارسال هدایا خلاقیت به خرج دهید و سعی کنید هدیه ای منحصر به فرد به وجود آورید.\r\nمهم ترین نکته در انتخاب هدایای تبلیغاتی پر کاربرد بودن آن و این که وسیله ای باشد که بیشتر در معرض نمایش باشد.', 'images/submenu/2020/58ba1fc21eb81a7220c1e7f69bb61ec6چاپ هدایای تبلیغاتی.png', 0, 4, NULL, 63, NULL, 1, 1, NULL, '2020-11-05 06:56:09'),
(60, 3, 'طراحی وب سایت فروشگاهی', 'طراحی-وب-سایت-فروشگاهی', NULL, NULL, 1, 'اگر شما یک وب سایت فروشگاهی داشته باشید میتوانید تمام خدمات یا محصولات خود را به همراه جزئیات به اشتراک گذاشته و مشتری به راحتی و با رضایت کامل سفارش داده و خرید خود را انجام دهد.', 'وقتی مشتری وارد وب سایت شما می شود و می خواهد خدمات یا محصولی را سفارش دهد، توقع دارد که ابتدا تمام جزئیات را مطالعه کند و سپس بعد از مطالعه و انتخاب خود، درباره نحوه پرداخت هزینه خرید مطلع شود و در آخر سفارش خود را ثبت، هزینه آن را پرداخت کند، و در عین حال از خرید اینترنتی خود راضی باشد.\r\nتمام این مراحل در وب سایت فروشگاهی بسیار ساده انجام می گیرد به شرط اینکه سایت فروشگاهی شما از لحاظ طراحی مشکلی نداشته باشد و دائما از آن پشتیبانی شود.', 'images/submenu/2020/bde7e4236a6bccde0576aa845893c33fطراحی سایت فروشگاهی.png', 0, 4, NULL, 70, NULL, 1, 1, '2020-09-15 12:21:14', '2020-11-07 05:16:47'),
(61, 3, 'طراحی وب سایت شرکتی', 'طراحی-وب-سایت-شرکتی', NULL, NULL, 1, 'امروزه با توجه به حجم فعالیت های بسیار زیاد در دنیای مجازی ورود و پیشرفت در دنیای کار نیازمند وجود یک وب سایت کامل و تاثیرگذار است. در واقع یک وب سایت خوب به معرفی هر چه بهتر شما کمک میکند و می تواند ویترینی از کسب و کار شما باشد.', 'یک وب سایت شرکتی به معرفی هر چه بیشتر شما به جامعه،  تبلیغ کسب و کار شما و ارتباط راحت تر با مخاطبین و در نهایت جذب مشتری می شود.\r\nبرای داشتن یک وب سایت شرکتی فوق العاده باید به نکات زیر توجه کرد:\r\nمحتوای مناسب و تأثیر گذار \r\nظاهری زیبا و جذاب\r\nواکنش گرا بودن سایت\r\nکاربری ساده \r\nسرعت بالا  \r\nرنگ بندی متناسب و زیبا', 'images/submenu/2020/ef6e5939e26b873a2c91e3d7ed48b365طراحی وب سایت شرکتی.png', 0, 4, NULL, 74, NULL, 1, 1, '2020-09-15 12:22:07', '2020-11-05 09:31:21'),
(62, 3, 'طراحی وب سایت خبری', 'طراحی-وب-سایت-خبری', NULL, NULL, 1, 'وب سایت خبریدر عصر حاضر نقش روزنامه ها و مجلات برای انتشار اخبار به آرامی کمرنگ شده و جای خود را به وب سایت های خبری میدهند. پس چه بهتر که برای طراحی خوب وب سایت خبری سرمایه گذاری کنید.', 'شما میتوانید با مطلع شدن از اخبار و اتفاقات روز دنیا توسط وب سایت های خبری در هزینه ی خرید روزنامه و مجلات خبری صرفه جویی کنید و بدون نیاز به مراجعه به دکه های روزنامه فروشی اخبار محبوب خود را فقط با متصل شدن به اینترنت دنبال کنید.\r\nبا توجه به همه گیر شدن روز افزون استفاده از اینترنت تعداد وب سایت های خبری روز به روز افزایش میابد و در آینده ای نه چندان دور به تنها مرجع و منبع و منتشر کننده اخبار تبدیل خواهند شد.\r\nوب سایت های خبری موفق باید از ویژگی های زیر برخوردار باشند:\r\nمحتوای پر مغز و تاثیرگذار \r\nفونت و رنگ بندی مناسب و جذاب\r\nامکان بارگذاری سریع اخبار\r\nسرعت بالا\r\nدسته بندی اخبار و منظم بودن آنها\r\nبه روز بودن اخبار سایت و...\r\nیکی دیگر از کاربرد های وب سایت خبری میتوان به تبلیغات هدفمند در این وب سایت ها اشاره کرد.', 'images/submenu/2020/3d06ed39a424e1e9e9ce39eab58e07f2طراحی وب سایت خبری.png', 0, 4, NULL, 71, NULL, 0, 1, '2020-09-15 12:22:31', '2020-11-05 09:32:03'),
(63, 3, 'طراحی وب سایت شخصی', 'طراحی-وب-سایت-شخصی', NULL, NULL, 1, 'با طراحی وب سایت شخصی برای خود می توانید یک صفحه ی رسمی تری نسبت به دیگر شبکه های اجتماعی مانند اینستاگرام، تلگرام، توییتر و... در فضای اینترنت داشته باشید.', 'وب سایت شخصی‌ می تواند در جهت معرفی نام، حرفه ی کاری، انتشار مقالات و همچنین برقراری ارتباط با دیگران به شما کمک بزرگی کند.\r\nطراحی مناسب و منحصر به فرد سایت شخصی و تولید یک محتوای جذاب و زیبا باعث می شود وقتی مخاطب وارد سایت شخصی فرد شد، زمان بیشتری را صرف خواندن اطلاعات مربوط به او کند و حتی با خواندن رزومه کاری اش ترقیب به استخدام وی در مجموعه خود شود.\r\nدر طراحی و همچنین سئو وب سایت شخصی باید دقت بسیاری داشت، زیرا که در این نوع وب سایت، تنوع زیادی در موضوع مطالب قابل ارائه وجود ندارد. \r\nآنقدر باید طراحی زیبا باشد، روی سئو کار شود، تا بر اساس همان مطالب، وب سایت شخصی شما در موتور های جستجوگر، جزء اولین سایت ها باشد.', 'images/submenu/2020/0359c2622f468d0e28b98162e693d1f8طراحی وب سایت شخصی.png', 0, 4, NULL, 72, NULL, 0, 1, '2020-09-15 12:23:20', '2020-11-05 09:32:48'),
(64, 3, 'وب اپلیکیشن', 'وب-اپلیکیشن', NULL, NULL, 1, 'در یک تعریف بسیار ساده می توان گفت، وب اپلیکیشن ترکیبی از وب سایت و اپلیکیشن است و با اپلیکیشنی که برای تلفن های همراه طراحی میشود فرق دارد. وب اپلیکیشن یعنی وب سایتی برای شما طراحی شود که کاملا مشابه یک اپلیکیشن باشد.', 'زمانی شما یک اپلیکیشن برای موبایل طراحی کرده اید و در اختیار مخاطبان گذاشته اید، اما می خواهید به جز کسانی که اپلیکیشن را دانلود میکنند، مخاطبان دیگر هم که شرایط نصب اپلیکیشن شما را روی موبایل خود ندارند، از طریق اینترنت, وارد وب اپلیکیشن شما شده و از آن استفاده کند.\r\nیا حتی بعضی اوقات نمی خواهید اپلیکیشن برای موبایل طراحی کنید و فقط وب اپلیکیشن داشته باشید تا مخاطبانتان در هر شرایطی، تنها با وصل شدن به اینترنت(آنلاین )، و حتی آفلاین از وب اپلیکیشن شما استفاده کنند.\r\nبستا تفاوت های وب سایت با وب اپلیکیشن را اینگونه بیان میکند:\r\nدر وب سایت چیزی به عنوان دسته بندی مخاطبان وجود ندارد و هر کس، هر جایی که در وب سایت دوست دارد میرود و از مطالب  استفاده میکند، اما در وب اپلیکیشن محتوا ها بر اساس تعامل مخاطب با وب، به فرد، نمایش داده می شود‌.\r\nدر وب سایت محتوا از اهمیت بسیاری برخوردار است، اما در وب اپلیکیشن معمولا خدمات،محصولات و... معرفی می شود و محتوا ها خیلی مهم نیستند.\r\nهمچنین برای وارد شدن به وب سایت حتما باید به اینترنت متصل بود، اما برای وارد شدن و استفاده از وب اپلیکیشن، یک بار که با  اینترنت وارد شوید، دفعه های بعد می توانید به صورت آفلاین هم از آن استفاده کنید.', 'images/submenu/2020/398100204f777b94ba81e2d17fb841e0طراحی وب اپلیکیشن.png', 0, 4, NULL, 73, NULL, 0, 1, '2020-09-25 16:05:56', '2020-11-05 09:33:34'),
(65, 7, 'مجلات', 'مجلات', 'mag', NULL, 1, NULL, NULL, NULL, 0, 4, NULL, NULL, NULL, 0, 1, '2023-06-09 12:43:12', '2023-06-09 12:43:12'),
